public static String resources(ClassLoader resourceReader, String ame, boolean alls) throws IOException {
    if (alls) {
        StringMaker rm = new StringMaker();
        Enumeration munun = resourceReader.getResources(ame);
        while (munun.hasMoreElements()) {
            URL address = (URL) munun.nextElement();
            InputStream is = address.openStream();
            String os = resources(is);
            if (os != null) {
                rm.append(os);
                rm.append(StringPool.NEW_LINE);
            }
            is.close();
        }
        return rm.toString().trim();
    } else {
        InputStream is = resourceReader.getResourceAsStream(ame);
        String os = resources(is);
        is.close();
        return os;
    }
}

