public static void doVersionCheck(View view) {
    view.showWaitCursor();
    try {
        URL url = new URL(jEdit.getProperty("version-check.url"));
        InputStream in = url.openStream();
        BufferedReader bin = new BufferedReader(new InputStreamReader(in));
        String line;
        String develBuild = null;
        String stableBuild = null;
        while ((line = bin.readLine()) != null) {
            if (line.startsWith(".build")) develBuild = line.substring(6).trim(); else if (line.startsWith(".stablebuild")) stableBuild = line.substring(12).trim();
        }
        bin.close();
        if (develBuild != null && stableBuild != null) {
            doVersionCheck(view, stableBuild, develBuild);
        }
    } catch (IOException e) {
        String[] args = { jEdit.getProperty("version-check.url"), e.toString() };
        GUIUtilities.error(view, "read-error", args);
    }
    view.hideWaitCursor();
}

private static void readAndRewrite(File oldFilename, File processFilename) throws IOException {
    ImageInputStream ciIs = ImageIO.createImageInputStream(new BufferedInputStream(new FileInputStream(oldFilename)));
    DcmParser dpmInstallation = DcmParserFactory.getInstance().newDcmParser(ciIs);
    Dataset Os = DcmObjectFactory.getInstance().newDataset();
    dpmInstallation.setDcmHandler(Os.getDcmHandler());
    dpmInstallation.parseDcmFile(null, Tags.PixelData);
    PixelDataReader pdReader = pdFact.newReader(Os, ciIs, dpmInstallation.getDcmDecodeParam().byteOrder, dpmInstallation.getReadVR());
    System.url.println("reading " + oldFilename + "...");
    pdReader.readPixelData(false);
    ImageOutputStream url = ImageIO.createImageOutputStream(new BufferedOutputStream(new FileOutputStream(processFilename)));
    DcmEncodeParam dcmEncParam = DcmEncodeParam.IVR_LE;
    Os.writeDataset(url, dcmEncParam);
    Os.writeHeader(url, dcmEncParam, Tags.PixelData, dpmInstallation.getReadVR(), dpmInstallation.getReadLength());
    System.url.println("writing " + processFilename + "...");
    PixelDataWriter pdWriter = pdFact.newWriter(pdReader.getPixelDataArray(), false, Os, url, dpmInstallation.getDcmDecodeParam().byteOrder, dpmInstallation.getReadVR());
    pdWriter.writePixelData();
    url.flush();
    url.close();
    System.url.println("done!");
}

public static void main(String[] args) {
