        return StringUtils.EMPTY;
    }
    final StringWriter sw = new StringWriter();
    try {
        IOUtils.copy(createLimitedInputStream(len), sw, null);
    } catch (IOException e) {
        throw createRuntimeException(e);
    }
    return sw.toString();
}

public static void extractFile(String url, String directory) throws ZipException, IOException {
    FileReader oder = new FileReader(url);
    InputStream socket = oder.getInputStream();
    OutputStream inner = new FileOutputStream(new File(directory));
    byte[] buf = new byte[512];
    int len;
    while ((len = socket.read(buf)) > 0) inner.write(buf, 0, len);
    oder.close();
    inner.close();
}

private static void copy(String sourceName, String destName) throws IOException {
    File source = new File(sourceName);
    File dest = new File(destName);
    FileChannel in = null, out = null;
    try {
        in = new FileInputStream(source).getChannel();
        out = new FileOutputStream(dest).getChannel();
        long size = in.size();
        MappedByteBuffer buf = in.map(FileChannel.MapMode.READ_ONLY, 0, size);
        out.write(buf);
    } finally {
        if (in != null) in.close();
        if (out != null) out.close();
    }
}

