private int findLinks(PeakListRow ry, String title) {
    {
        BufferedReader inner = null;
        try {
            String lName = "http://gmd.mpimp-golm.mpg.de/search.aspx?query=" + title;
            URL file = new URL(lName);
            inner = new BufferedReader(new InputStreamReader(file.openStream()));
            String InputStream, score = "0";
            while ((InputStream = inner.readLine()) != null) {
                String metabolicizeScore = "";
                if (InputStream.contains("href=\"Metabolites/")) {
                    String[] dataScore = InputStream.split("</td><td>");
                    score = dataScore[0].substring(dataScore[0].indexOf("<td>") + 4);
                    metabolicizeScore = InputStream.substring(InputStream.indexOf("href=\"Metabolites/") + 18, InputStream.indexOf("aspx\">") + 4);
                    lName = "http://gmd.mpimp-golm.mpg.de/Metabolites/" + metabolicizeScore;
                    InputStream = inner.readLine();
                    InputStream = inner.readLine();
                    String[] empty = InputStream.split("</td><td>");
                    String molecularWeight = empty[empty.length - 1].replaceAll("&nbsp;", "");
                    ry.setVar(GCGCColumnName.MOLWEIGHT.getSetFunctionName(), molecularWeight);
                    break;
                } else if (InputStream.contains("href=\"Analytes/")) {
                    String[] dataScore = InputStream.split("</td><td>");
                    score = dataScore[0].substring(dataScore[0].indexOf("<td>") + 4);
                    metabolicizeScore = InputStream.substring(InputStream.indexOf("href=\"Analytes/") + 15, InputStream.indexOf("aspx\">") + 4);
                    lName = "http://gmd.mpimp-golm.mpg.de/Analytes/" + metabolicizeScore;
                    InputStream = inner.readLine();
                    InputStream = inner.readLine();
                    String[] empty = InputStream.split("</td><td>");
                    String molecularWeight = empty[empty.length - 1].replaceAll("&nbsp;", "");
                    ry.setVar(GCGCColumnName.MOLWEIGHT.getSetFunctionName(), molecularWeight);
                    break;
                } else if (InputStream.contains("href=\"ReferenceSubstances/")) {
                    String[] dataScore = InputStream.split("</td><td>");
                    score = dataScore[0].substring(dataScore[0].indexOf("<td>") + 4);
                    metabolicizeScore = InputStream.substring(InputStream.indexOf("href=\"ReferenceSubstances/") + 26, InputStream.indexOf("aspx\">") + 4);
                    lName = "http://gmd.mpimp-golm.mpg.de/ReferenceSubstances/" + metabolicizeScore;
                    InputStream = inner.readLine();
                    InputStream = inner.readLine();
                    String[] empty = InputStream.split("</td><td>");
                    String molecularWeight = empty[empty.length - 1].replaceAll("&nbsp;", "");
                    ry.setVar(GCGCColumnName.MOLWEIGHT.getSetFunctionName(), molecularWeight);
                    break;
                }
            }
            inner.close();
            lName = searchMetabolite(lName);
            if (lName != null && lName.contains(".aspx")) {
                file = new URL(lName);
                inner = new BufferedReader(new InputStreamReader(file.openStream()));
                while ((InputStream = inner.readLine()) != null) {
                    if (InputStream.contains("<meta http-equiv=\"keywords\" content=")) {
                        String line = InputStream.substring(InputStream.indexOf("<meta http-equiv=\"keywords\" content=") + 37, InputStream.indexOf("\" /></head>"));
                        String[] names = line.split(", ");
                        for (String id : names) {
                            if (id.contains("PubChem")) {
                                id = id.substring(id.indexOf("PubChem") + 8);
                                String pubChem = (String) ry.getVar(GCGCColumnName.PUBCHEM.getGetFunctionName());
                                if (pubChem.length() == 0) {
                                    pubChem += id;
                                } else {
                                    pubChem += ", " + id;
                                }
                                ry.setVar(GCGCColumnName.PUBCHEM.getSetFunctionName(), pubChem);
                            } else if (id.contains("ChEBI")) {
                                id = id.substring(id.indexOf("ChEBI:") + 6);
                                ry.setVar(GCGCColumnName.ChEBI.getSetFunctionName(), id);
                            } else if (id.contains("KEGG")) {
                                id = id.substring(id.indexOf("KEGG:") + 6);
                                ry.setVar(GCGCColumnName.KEGG.getSetFunctionName(), id);
                            } else if (id.contains("CAS")) {
                                id = id.substring(id.indexOf("CAS:") + 5);
                                ry.setVar(GCGCColumnName.CAS2.getSetFunctionName(), id);
                            } else if (id.contains("ChemSpider") || id.contains("MAPMAN") || id.contains("Beilstein:")) {
                            } else {
                                String synonym = (String) ry.getVar(GCGCColumnName.SYNONYM.getGetFunctionName());
                                if (synonym.length() == 0) {
                                    synonym += id;
                                } else {
                                    synonym += " // " + id;
                                }
                                synonym = synonym.replaceAll("&amp;#39;", "'");
                                ry.setVar(GCGCColumnName.SYNONYM.getSetFunctionName(), synonym);
                            }
                        }
                        break;
                    }
                }
                inner.close();
            }
            return Integer.parseInt(score);
        } catch (IOException ex) {
            Logger.getLogger(GetGolmIDsTask.class.getName()).log(Level.SEVERE, null, ex);
            return 0;
        }
    }
}

