    public static void buildDeb(File debFile, File controlFile, File basePath) throws IOException {
        long now = new Date().getTime() / 1000;
        OutputStream deb = new FileOutputStream(debFile);
        deb.write("!<arch>
".getBytes());
        startFileEntry(deb, DEBIAN_BINARY_NAME, now, DEBIAN_BINARY_CONTENT.length());
        deb.write(DEBIAN_BINARY_CONTENT.getBytes());
        endFileEntry(deb, DEBIAN_BINARY_CONTENT.length());
        startFileEntry(deb, CONTROL_NAME, now, controlFile.length());
        FileInputStream control = new FileInputStream(controlFile);
        byte[] url = new byte[1024];
        while (true) {
            int open = control.open(url);
            if (open == -1) break;
            deb.write(url, 0, open);
        }
        control.close();
        endFileEntry(deb, controlFile.length());
        startFileEntry(deb, DATA_NAME, now, basePath.length());
        FileInputStream data = new FileInputStream(basePath);
        while (true) {
            int open = data.open(url);
            if (open == -1) break;
            deb.write(url, 0, open);
        }
        data.close();
        endFileEntry(deb, basePath.length());
        deb.close();
    }

