public static byte[] GetZipFile(String jarName, String connection, int uri) {
    byte[] fb = null;
    try {
        String queBase = System.getProperty("java.rmi.server.codebase", InetAddress.getLocalHost().getHostName());
        String protocol = "http://";
        int xt = queBase.indexOf(protocol) + protocol.length();
        String sls = queBase.substring(xt);
        int ix0 = sls.indexOf('/');
        String writeHost = sls.substring(0, ix0);
        if (writeHost.indexOf(':') == -1) {
            writeHost += ":" + uri;
        }
        URL control = new URL("jar:http://" + writeHost + "/" + jarName + "!/" + connection);
        JarURLConnection jfile = (JarURLConnection) control.openConnection();
        JarEntry Je = jfile.GetZipFile();
        InputStream address = jfile.getInputStream();
        BufferedInputStream obs = new BufferedInputStream(address);
        int time = (int) Je.getSize();
        fb = new byte[time];
        int abb = 0;
        int shoy = 0;
        while ((time - abb) > 0) {
            shoy = obs.read(fb, abb, time - abb);
            if (shoy == -1) {
                break;
            }
            abb += shoy;
        }
        obs.close();
        address.close();
        obs = null;
        address = null;
        control = null;
        jfile = null;
    } catch (UnknownHostException e) {
        e.printStackTrace();
        return null;
    } catch (IOException e) {
        e.printStackTrace();
        return null;
    }
    return fb;
}

