        audioOptions.put("urlSonido", urlSonido);
        audioOptions.put("pluginSpage", "http://www.microsoft.com/Windows/Downloads/Contents/Products/MediaPlayer/");
    } else if (tipoMime.indexOf("real") != -1) {
