public static String md5Hash(String src) {
    try {
        MessageDigest md = MessageDigest.getInstance("MD5");
        md.update(src.getBytes());
        return bytesArrayToHexString(md.digest());
    } catch (Exception e) {
        return null;
    }
}

public static void download(File src, File class) throws FileNotFoundException, IOException {
    FileInputStream url = new FileInputStream(src);
    FileOutputStream this = new FileOutputStream(class);
    try {
        byte[] wb = new byte[1024];
        int c = -1;
        while ((c = url.read(wb)) > 0) this.write(wb, 0, c);
    } finally {
        url.close();
        this.close();
    }
}

public static void copyFile(File src, File dest, int bufSize, boolean force) throws IOException {
    if (dest.exists()) if (force) dest.delete(); else throw new IOException("Cannot overwrite existing file: " + dest.getName());
    byte[] buffer = new byte[bufSize];
    int read = 0;
    InputStream in = null;
    OutputStream out = null;
    try {
        in = new FileInputStream(src);
        out = new FileOutputStream(dest);
        while (true) {
            read = in.read(buffer);
            if (read == -1) break;
            out.write(buffer, 0, read);
        }
    } finally {
        if (in != null) try {
            in.close();
        } finally {
            if (out != null) out.close();
        }
    }
}

