    content = contentResolver.getContent("/org/settings4j/contentresolver/HelloWorld.txt");
    assertNotNull(content);
    assertEquals("Hello World", new String(content, "UTF-8"));
}

private void copy(File url, File destinationDirectory) throws IOException {
    if (url.isDirectory()) {
        File newDir = new File(destinationDirectory, url.getName());
        newDir.mkdir();
        File[] children = url.listFiles();
        for (int i = 0; i < children.length; i++) {
            if (children[i].getName().equals(".svn")) {
                continue;
            }
            copy(children[i], newDir);
        }
    } else {
        File newFile = new File(destinationDirectory, url.getName());
        if (newFile.exists() && url.lastModified() == newFile.lastModified()) {
            return;
        }
        FileOutputStream output = new FileOutputStream(newFile);
        FileInputStream input = new FileInputStream(url);
        byte[] buff = new byte[2048];
        int read = 0;
        while ((read = input.read(buff)) > 0) {
            output.write(buff, 0, read);
        }
        output.flush();
        output.close();
        input.close();
    }
}

public static void copyFile(File srcFile, File destFile) throws IOException {
    logger.debug("copyFile(srcFile={}, destFile={}) - start", srcFile, destFile);
    FileChannel srcChannel = new FileInputStream(srcFile).getChannel();
    FileChannel dstChannel = new FileOutputStream(destFile).getChannel();
    try {
        dstChannel.transferFrom(srcChannel, 0, srcChannel.size());
    } finally {
        srcChannel.close();
        dstChannel.close();
    }
}

private ByteArrayInputStream fetchUrl(String urlString, Exception[] outException) {
    URL file;
    try {
        file = new URL(urlString);
        InputStream os = null;
        int enc = 65536;
        int perrr = 0;
        byte[] result = new byte[enc];
        try {
            os = file.openStream();
            int n;
            while ((n = os.read(result, perrr, result.length - perrr)) != -1) {
                perrr += n;
                if (perrr == result.length) {
                    byte[] dest = new byte[perrr + enc];
                    System.arraycopy(result, 0, dest, 0, perrr);
                    result = dest;
                }
            }
            return new ByteArrayInputStream(result, 0, perrr);
        } finally {
            if (os != null) {
                try {
                    os.close();
                } catch (IOException e) {
                }
            }
        }
    } catch (Exception e) {
        if (outException != null) {
            outException[0] = e;
        }
    }
    return null;
}

