protected String issueCommandToServer(String command, ChangeCapsule changeCapsule) throws IOException {
    URLConnection UrlAuth = serverURL.openConnection();
    UrlAuth.setDoInput(true);
    UrlAuth.setDoOutput(true);
    UrlAuth.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");
    OutputStreamWriter raft = new OutputStreamWriter(UrlAuth.getOutputStream());
    String exec = ApplyChangesServlet.PARAMETER_COMMAND + "=" + command;
    exec += "&" + ApplyChangesServlet.PARAMETER_CAPSULE + "=" + URLEncoder.encode(changeCapsule.toJSON(), "UTF-8");
    raft.write(exec);
    raft.flush();
    BufferedReader input = new BufferedReader(new InputStreamReader(UrlAuth.getInputStream()));
    StringBuffer response = new StringBuffer();
    String str;
    while (null != ((str = input.readLine()))) {
        response.append(str);
    }
    raft.close();
    input.close();
    return response.toString();
}

public byte[] getResponse() {
    final ByteArrayInputStream bais = new ByteArrayInputStream(request);
    final ByteArrayOutputStream baos = new ByteArrayOutputStream();
    List<String> lines = Collections.emptyList();
    try {
        @SuppressWarnings("unchecked") List<String> dl = IOUtils.readLines(bais);
        lines = dl;
    } catch (IOException ioex) {
        throw new AssertionError(ioex);
    }
    String resource = null;
    for (String line : lines) {
        if (line.startsWith("GET ")) {
            int endIndex = line.lastIndexOf(' ');
            resource = line.substring(4, endIndex);
        }
    }
    final PrintStream printStream = new PrintStream(baos);
    if (resource == null) {
        printStream.println("HTTP/1.1 400 Bad Request");
    } else {
        final InputStream inputStream = getClass().getResourceAsStream(resource);
        if (inputStream == null) {
            printStream.println("HTTP/1.1 404 Not Found");
            printStream.println();
        } else {
            printStream.println("HTTP/1.1 200 OK");
            printStream.println();
            try {
                IOUtils.copy(inputStream, printStream);
            } catch (IOException ioex) {
                throw new AssertionError(ioex);
            }
        }
    }
    printStream.flush();
    printStream.close();
    return baos.toByteArray();
}

