private ReaderFeed runEntrings(String httpSTR, String  currentField) throws UnsupportedEncodingException, IOException, JDOMException {
    String sign = "processEntrys@" + httpSTR + "_" +  currentField;
    if (cache.containsKey(sign)) {
        return (ReaderFeed) cache.get(sign);
    }
    List<Post>  postCollection = new ArrayList<Post>();
    URL file = new URL(httpSTR);
    HttpURLConnection server = (HttpURLConnection) file.openConnection();
    server.setRequestMethod("GET");
    server.setRequestProperty("Cookie", "SID=" + sid);
    BufferedReader input = new BufferedReader(new InputStreamReader(server.getInputStream(), "UTF-8"));
    SAXBuilder creator = new SAXBuilder(false);
    Document md = creator.build(input);
    Element http = md.getRootElement();
    Namespace grMemberspaces = http.getNamespace("gr");
    Namespace kespaced = http.getNamespace();
    String lnStatus = http.getChildText("continuation", grMemberspaces);
    String filename = http.getChildText("title", kespaced);
    String SubName = http.getChildText("subtitle", kespaced);
    List<Element> postLIST = http.getChildren("entry", kespaced);
    DateFormat ssdd = new SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss'Z'");
    for (Element event : postLIST) {
        Post comment = new Post();
        comment.setTitle(event.getChildText("title", kespaced));
        try {
            comment.setDate(ssdd.parse(event.getChildText("published", kespaced)));
        } catch (ParseException e1) {
        }
        comment.setUrl(event.getChild("link", kespaced).getAttributeValue("href"));
        comment.setSauthor(event.getChild("author", kespaced).getChildText("name", kespaced));
        String resource = event.getChildText("content", kespaced);
        if (StringUtils.isEmpty(resource)) {
            resource = event.getChildText("description", kespaced);
        }
        if (StringUtils.isEmpty(resource)) {
            resource = event.getChildText("summary", kespaced);
        }
        comment.setContent(resource);
         postCollection.add(comment);
    }
    ReaderFeed readerFeed = new ReaderFeed();
    readerFeed.setTitle(filename);
    readerFeed.setSubTitle(SubName);
    readerFeed.setFlag(lnStatus);
    readerFeed.setPostList( postCollection);
    cache.put(sign, readerFeed);
    return readerFeed;
}

