public void loadExistingAntlibs(ClassLoader classLoader) {
    URL antlibUrl;
    URI antlibUri;
    try {
        Enumeration<URL> resources = classLoader == null ? ClassLoader.getSystemResources(antLibsResource) : classLoader.getResources(antLibsResource);
        while (resources.hasMoreElements()) {
            URL url = resources.nextElement();
            InputStream stream = url.openStream();
            BufferedReader reader = new BufferedReader(new InputStreamReader(stream, "UTF-8"));
            for (String line = reader.readLine(); line != null; line = reader.readLine()) {
                String pkg = line.trim();
                URI uri = URI.create("antlib:" + pkg);
                URI resource2antlib = URI.create(antLibsResource2root + pkg.replace('.', '/') + (pkg.isEmpty() ? "" : "/") + "antlib.xml");
                antlibUri = NetUtils.resolve(url.toURI(), resource2antlib);
                try {
                    antlibUrl = antlibUri.toURL();
                } catch (IllegalArgumentException e) {
                    System.err.println("base uri: " + url);
                    System.err.println("relativepath: " + resource2antlib);
                    System.err.println("target uri: " + antlibUri);
                    throw new RuntimeException(antlibUri.toString(), e);
                }
                loadAntLib(antlibUrl, uri);
            }
            reader.close();
            stream.close();
        }
    } catch (IOException e) {
        throw new RuntimeException(e);
    } catch (URISyntaxException e) {
        throw new RuntimeException(e);
    }
}

protected ActionForward submitExecute(ActionMapping  matching, ActionForm url, HttpServletRequest input, HttpServletResponse xml) throws Exception {
    ActionMessages  messenger = new ActionMessages();
    try {
        boolean isMultipost = FileUpload.isMultipartContent(input);
        Mail mailinstance = getMailInstance(input);
        if (isMultipost) {
            Map uploads = new HashMap();
            Vector attachment = new Vector();
            List plugins = diskFileUpload.parseRequest(input);
            Iterator iter = plugins.iterator();
            while (iter.hasNext()) {
                FileItem item = (FileItem) iter.next();
                if (item.isFormField()) {
                    if (item.getFieldName().equals("forwardAttachments")) {
                        String[] aux = item.getString().split(":");
                        MailPartObj html = mailinstance.getAttachment(aux[0], aux[1]);
                        attachment.addElement(html);
                    } else {
                        uploads.put(item.getFieldName(), item.getString());
                    }
                } else {
                    if (!StringUtils.isBlank(item.getName())) {
                        ByteArrayOutputStream baos = null;
                        try {
                            baos = new ByteArrayOutputStream();
                            IOUtils.copy(item.getInputStream(), baos);
                            MailPartObj html = new MailPartObj();
                            html.setAttachent(baos.toByteArray());
                            html.setContentType(item.getContentType());
                            html.setName(item.getName());
                            html.setSize(item.getSize());
                            attachment.addElement(html);
                        } catch (Exception ex) {
                        } finally {
                            IOUtils.closeQuietly(baos);
                        }
                    }
                }
            }
            String body = "";
            if (uploads.get("taBody") != null) {
                body = (String) uploads.get("taBody");
            } else if (uploads.get("taReplyBody") != null) {
                body = (String) uploads.get("taReplyBody");
            }
            Preferences preferencesInstance = getPreferencesInstance(input);
            Send sendInstance = getSendInstance(input);
            String mid = (String) uploads.get("mid");
            if (StringUtils.isBlank(mid)) {
                input.setAttribute("action", "compose");
            } else {
                input.setAttribute("action", "reply");
            }
            Boolean isHtml = null;
            if (StringUtils.isBlank((String) uploads.get("isHtml"))) {
                isHtml = new Boolean(preferencesInstance.getPreferences().isHtmlMessage());
            } else {
                isHtml = Boolean.valueOf((String) uploads.get("isHtml"));
            }
            sendInstance.send(mid, Integer.parseInt((String) uploads.get("identity")), (String) uploads.get("to"), (String) uploads.get("cc"), (String) uploads.get("bcc"), (String) uploads.get("subject"), body, attachment, isHtml.booleanValue(), Charset.defaultCharset().displayName(), (String) uploads.get("priority"));
        } else {
             messenger.add("general", new ActionMessage(ExceptionCode.ERROR_MESSAGES_PREFIX + "mail.send", "The form is null"));
            input.setAttribute("exception", "The form is null");
            input.setAttribute("newLocation", null);
            doTrace(input, DLog.ERROR, getClass(), "The form is null");
        }
    } catch (Exception ex) {
        String errorMessage = ExceptionUtilities.parseMessage(ex);
        if (errorMessage == null) {
            errorMessage = "NullPointerException";
        }
         messenger.add("general", new ActionMessage(ExceptionCode.ERROR_MESSAGES_PREFIX + "general", errorMessage));
        input.setAttribute("exception", errorMessage);
        doTrace(input, DLog.ERROR, getClass(), errorMessage);
    } finally {
    }
    if ( messenger.isEmpty()) {
        doTrace(input, DLog.INFO, getClass(), "OK");
        return  matching.findForward(Constants.ACTION_SUCCESS_FORWARD);
    } else {
        saveErrors(input,  messenger);
        return  matching.findForward(Constants.ACTION_FAIL_FORWARD);
    }
}

private void extractZipFile(String filename, JTextPane progressText) throws IOException {
    String destinationname = "";
    byte[] buf = new byte[1024];
    ZipInputStream zipinputstream = null;
    ZipEntry zipentry;
    zipinputstream = new ZipInputStream(new FileInputStream(filename));
    while ((zipentry = zipinputstream.getNextEntry()) != null) {
        String entryName = zipentry.getName();
        if (progressText != null) {
            progressText.setText("extracting " + entryName);
        }
        int n;
        FileOutputStream fileoutputstream;
        if (zipentry.isDirectory()) {
            (new File(destinationname + entryName)).mkdir();
            continue;
        }
        fileoutputstream = new FileOutputStream(destinationname + entryName);
        while ((n = zipinputstream.read(buf, 0, 1024)) > -1) fileoutputstream.write(buf, 0, n);
        fileoutputstream.close();
        zipinputstream.closeEntry();
    }
    if (progressText != null) {
        progressText.setText("Files extracted");
    }
    zipinputstream.close();
}

