public void handleHandshake(Packet2Handshake par1Packet2handle) {
    boolean key = true;
    String sum = par1Packet2handle.username;
    if (sum == null || sum.trim().length() == 0) {
        key = false;
    } else if (!sum.equals("-")) {
        try {
            Long.parseLong(sum, 16);
        } catch (NumberFormatException numberformatexception) {
            key = false;
        }
    }
    if (!key) {
        netManager.networkShutdown("disconnect.genericReason", new Object[] { "The server responded with an invalid server key" });
    } else if (par1Packet2handle.username.equals("-")) {
        addToSendQueue(new Packet1Login(mc.session.username, 29));
    } else {
        try {
            URL server = new URL((new StringBuilder()).append("http://session.minecraft.net/game/joinserver.jsp?user=").append(mc.session.username).append("&sessionId=").append(mc.session.sessionId).append("&serverId=").append(par1Packet2handle.username).toString());
            BufferedReader bufferedreader = new BufferedReader(new InputStreamReader(server.openStream()));
            String s1 = bufferedreader.readLine();
            bufferedreader.close();
            if (s1.equalsIgnoreCase("ok")) {
                addToSendQueue(new Packet1Login(mc.session.username, 29));
            } else {
                netManager.networkShutdown("disconnect.loginFailedInfo", new Object[] { s1 });
            }
        } catch (Exception exception) {
            exception.printStackTrace();
            netManager.networkShutdown("disconnect.genericReason", new Object[] { (new StringBuilder()).append("Internal client error: ").append(exception.toString()).toString() });
        }
    }
}

