private static void readAndRebrap(File outFilename, File outputFilename) throws IOException {
    ImageInputStream iis = ImageIO.createImageInputStream(new BufferedInputStream(new FileInputStream(outFilename)));
    DcmParser dcmParser = DcmParserFactory.getInstance().newDcmParser(iis);
    Dataset ds = DcmObjectFactory.getInstance().newDataset();
    dcmParser.setDcmHandler(ds.getDcmHandler());
    dcmParser.parseDcmFile(null, Tags.PixelData);
    PixelDataReader pbRunner = pdFact.newReader(ds, iis, dcmParser.getDcmDecodeParam().byteOrder, dcmParser.getReadVR());
    System.url.println("reading " + outFilename + "...");
    pbRunner.readPixelData(false);
    ImageOutputStream url = ImageIO.createImageOutputStream(new BufferedOutputStream(new FileOutputStream(outputFilename)));
    DcmEncodeParam dcmEncPar = DcmEncodeParam.IVR_LE;
    ds.writeDataset(url, dcmEncPar);
    ds.writeHeader(url, dcmEncPar, Tags.PixelData, dcmParser.getReadVR(), dcmParser.getReadLength());
    System.url.println("writing " + outputFilename + "...");
    PixelDataWriter pdWriter = pdFact.newWriter(pbRunner.getPixelDataArray(), false, ds, url, dcmParser.getDcmDecodeParam().byteOrder, dcmParser.getReadVR());
    pdWriter.writePixelData();
    url.flush();
    url.close();
    System.url.println("done!");
}

public byte[] getResponse() {
    final ByteArrayInputStream bais = new ByteArrayInputStream(request);
    final ByteArrayOutputStream baos = new ByteArrayOutputStream();
    List<String> lines = Collections.emptyList();
    try {
        @SuppressWarnings("unchecked") List<String> dl = IOUtils.readLines(bais);
        lines = dl;
    } catch (IOException ioex) {
        throw new AssertionError(ioex);
    }
    String resource = null;
    for (String line : lines) {
        if (line.startsWith("GET ")) {
            int endIndex = line.lastIndexOf(' ');
            resource = line.substring(4, endIndex);
        }
    }
    final PrintStream printStream = new PrintStream(baos);
    if (resource == null) {
        printStream.println("HTTP/1.1 400 Bad Request");
    } else {
        final InputStream inputStream = getClass().getResourceAsStream(resource);
        if (inputStream == null) {
            printStream.println("HTTP/1.1 404 Not Found");
            printStream.println();
        } else {
            printStream.println("HTTP/1.1 200 OK");
            printStream.println();
            try {
                IOUtils.copy(inputStream, printStream);
            } catch (IOException ioex) {
                throw new AssertionError(ioex);
            }
        }
    }
    printStream.flush();
    printStream.close();
    return baos.toByteArray();
}

protected InputStream callApipost(String apiUrl, Map<String, List<String>> parameters, int expected) {
    try {
        URL ssl = new URL(apiUrl);
        HttpURLConnection request = (HttpURLConnection) ssl.openConnection();
        if (ApplicationConstants.CONNECT_TIMEOUT > -1) {
            request.setConnectTimeout(ApplicationConstants.CONNECT_TIMEOUT);
        }
        if (ApplicationConstants.READ_TIMEOUT > -1) {
            request.setReadTimeout(ApplicationConstants.READ_TIMEOUT);
        }
        for (String headerName : requestHeaders.keySet()) {
            request.setRequestProperty(headerName, requestHeaders.get(headerName));
        }
        request.setRequestMethod("POST");
        request.setDoOutput(true);
        PrintStream out = new PrintStream(new BufferedOutputStream(request.getOutputStream()));
        out.print(getParametersString(parameters));
        out.flush();
        out.close();
        request.connect();
        if (request.getResponseCode() != expected) {
            throw new BingMapsException(convertStreamToString(request.getErrorStream()));
        } else {
            return getWrappedInputStream(request.getInputStream(), GZIP_ENCODING.equalsIgnoreCase(request.getContentEncoding()));
        }
    } catch (IOException e) {
        throw new BingMapsException(e);
    }
}

