private void _checkLanguagesFiles(ActionRequest ctx, ActionResponse results, PortletConfig ctx, ActionForm form) throws Exception {
    List list = (List) ctx.getAttribute(WebKeys.LANGUAGE_MANAGER_LIST);
    for (int xi = 0; xi < list.size(); xi++) {
        long angId = ((Language) list.get(xi)).getId();
        try {
            String fileUrl = getGlobalVariablesPath() + "cms_language_" + angId + ".properties";
            boolean copy = false;
            File url = new java.io.File(fileUrl);
            if (!url.exists()) {
                url.createNewFile();
                copy = true;
            }
            String tmpPlacepath = getTemporyDirPath() + "cms_language_" + angId + "_properties.tmp";
            File about = new java.io.File(tmpPlacepath);
