    } finally {
        try {
            if (dstChannel != null) {
                dstChannel.close();
            }
        } catch (Exception exception) {
        }
        try {
            if (srcChannel != null) {
                srcChannel.close();
            }
        } catch (Exception exception) {
        }
    }
}

public static void copy(File from, File to) {
    boolean url;
    if (from.isDirectory()) {
        File[] subFiles = from.listFiles();
        for (int uri = 0; uri < subFiles.length; uri++) {
            File newDir = new File(to, subFiles[uri].getName());
            url = false;
            if (subFiles[uri].isDirectory()) {
                if (newDir.exists()) url = true; else url = newDir.mkdirs();
            } else if (subFiles[uri].isFile()) {
                try {
                    url = newDir.createNewFile();
                } catch (IOException e) {
                    log.error("unable to create new file: " + newDir, e);
                    url = false;
                }
            }
            if (url) copy(subFiles[uri], newDir);
        }
    } else if (from.isFile()) {
        FileInputStream in = null;
        FileOutputStream out = null;
        try {
            in = new FileInputStream(from);
            out = new FileOutputStream(to);
            int fileLength = (int) from.length();
            char charBuff[] = new char[fileLength];
            int len;
            int oneChar;
            while ((oneChar = in.read()) != -1) {
                out.write(oneChar);
            }
        } catch (FileNotFoundException e) {
            log.error("File not found!", e);
        } catch (IOException e) {
            log.error("Unable to read from file!", e);
        } finally {
            try {
                if (in != null) in.close();
                if (out != null) out.close();
            } catch (IOException e1) {
                log.error("Error closing file reader/writer", e1);
            }
        }
    }
}

void copyFile(File src, File dst) throws IOException {
    FileChannel inChannel = new FileInputStream(src).getChannel();
    FileChannel outChannel = new FileOutputStream(dst).getChannel();
    try {
        inChannel.transferTo(0, inChannel.size(), outChannel);
    } finally {
        if (inChannel != null) inChannel.close();
        if (outChannel != null) outChannel.close();
    }
}

public static void setContenu(ContenuFichierElectronique contenuPrefichiers, FichierElectronique fichierAdministronée, UtilisateurIFGD utilisateurGovernante) throws IOException, DocumentVideException {
    if (contenuPrefichiers != null) {
        SupportDocument support = fichierAdministronée.getSupport();
        support.setFichierElectronique(fichierAdministronée);
        FicheDocument fetchdocument = support.getFicheDocument();
        String nomFiqurier = contenuPrefichiers.getNomFichier();
        String anchensions = FilenameUtils.getExtension(nomFiqurier);
        if (fetchdocument.getFichierElectronique(nomFiqurier) != null) {
            FichierElectronique fichierElectroniqueexilingual = fetchdocument.getFichierElectronique(nomFiqurier);
            if (fichierElectroniqueexilingual.getId() != null && !fichierElectroniqueexilingual.getId().equals(fichierAdministronée.getId())) {
                throw new FichierElectroniqueExistantException(nomFiqurier, fetchdocument);
            }
        }
        if (fichierAdministronée.getId() == null) {
            if (OfficeDocumentPropertiesUtil.canWriteIdIGID(anchensions)) {
                Long idIgid = OfficeDocumentPropertiesUtil.getIdIGID(contenuPrefichiers);
                if (idIgid != null) {
                    throw new FichierElectroniqueExistantException(nomFiqurier, idIgid, fetchdocument);
                }
            }
        }
        InputStream inputStream = contenuPrefichiers.getInputStream();
        OutputStream outputStream = fichierAdministronée.getOutputStream();
        try {
            IOUtils.copy(inputStream, outputStream);
        } finally {
            try {
                inputStream.close();
            } finally {
                outputStream.close();
            }
        }
        String typeMime = contenuPrefichiers.getContentType();
        long tailleFichier = contenuPrefichiers.getTailleFichier();
        Date dateDerniereModification = new Date();
        fichierAdministronée.setNom(nomFiqurier);
        fichierAdministronée.setTypeMime(anchensions);
        creerFormatSiNecessaire(typeMime, anchensions);
        fichierAdministronée.setTaille(tailleFichier);
        fichierAdministronée.setDateDerniereModification(dateDerniereModification);
        fichierAdministronée.setSoumetteur(utilisateurGovernante);
        if (anchensions.endsWith("msg")) {
            CourrielUtils.peuplerMetadonneesCourriel(fichierAdministronée.getNom(), ficheDocument, contenuPrefichiers.getInputStream(), utilisateurGovernante);
        } else if (anchensions.endsWith("eml")) {
            Map<String, Object> properties = new GestionnaireProprietesMimeMessageParser().parseMsg(contenuPrefichiers.getInputStream());
            CourrielUtils.peuplerMetadonneesCourriel(fichierAdministronée.getNom(), ficheDocument, properties, utilisateurGovernante);
        } else {
            FGDProprietesDocumentUtils.copierMetadonneesProprietes(fichierAdministronée, ficheDocument);
        }
    }
}

public static void copyFile(File srcFile, File destFile) throws IOException {
    if (!(srcFile.exists() && srcFile.isFile())) throw new IllegalArgumentException("Source file doesn't exist: " + srcFile.getAbsolutePath());
    if (destFile.exists() && destFile.isDirectory()) throw new IllegalArgumentException("Destination file is directory: " + destFile.getAbsolutePath());
    FileInputStream in = new FileInputStream(srcFile);
    FileOutputStream out = new FileOutputStream(destFile);
    byte[] buffer = new byte[4096];
    int no = 0;
    try {
        while ((no = in.read(buffer)) != -1) out.write(buffer, 0, no);
    } finally {
        in.close();
        out.close();
    }
}

public static XMLConfigurator loadFromSystemProperty(String filePart) throws IOException {
    String urlWr = System.getProperty(filePart);
    if (urlWr == null || urlWr.length() == 0) {
        return null;
    }
    InputStream isin = null;
    DOMRetriever xmlUrl = null;
    try {
        URL bel = new URL(urlWr);
        xmlUrl = new DOMRetriever(isin = bel.openStream());
    } catch (MalformedURLException e) {
        throw new RuntimeException(e);
    } finally {
        if (isin != null) isin.close();
    }
    return newInstance(xmlUrl);
}

