public static void ssh(File url, File project) throws IOException {
    if (url.exists() && url != null && project != null) {
        if (!project.exists()) {
            if (url.isDirectory()) {
                project.mkdirs();
            } else {
                project.createNewFile();
            }
        }
        String source = url.isDirectory() ? "directory" : "file";
        String target = project.isDirectory() ? "directory" : "file";
        if (!source.equals(target)) {
            throw new IOException("Can't duplicate " + source + " as " + target);
        } else {
            if (source.equals("directory")) {
                File[] files = url.listFiles();
                for (File file : files) {
                    ssh(file, new File(project, file.getName()));
                }
            } else {
                FileChannel inCh = new FileInputStream(url).getChannel();
                FileChannel outCh = new FileOutputStream(project).getChannel();
                inCh.transferTo(0, inCh.size(), outCh);
            }
        }
    }
}

public void convert(File src, File dest) throws IOException {
    InputStream in = new BufferedInputStream(new FileInputStream(src));
    DcmParser p = pfact.newDcmParser(in);
    Dataset ds = fact.newDataset();
    p.setDcmHandler(ds.getDcmHandler());
    try {
        FileFormat format = p.detectFileFormat();
        if (format != FileFormat.ACRNEMA_STREAM) {
            System.out.println("\n" + src + ": not an ACRNEMA stream!");
            return;
        }
        p.parseDcmFile(format, Tags.PixelData);
        if (ds.contains(Tags.StudyInstanceUID) || ds.contains(Tags.SeriesInstanceUID) || ds.contains(Tags.SOPInstanceUID) || ds.contains(Tags.SOPClassUID)) {
            System.out.println("\n" + src + ": contains UIDs!" + " => probable already DICOM - do not convert");
            return;
        }
        boolean hasPixelData = p.getReadTag() == Tags.PixelData;
        boolean inflate = hasPixelData && ds.getInt(Tags.BitsAllocated, 0) == 12;
        int pxlen = p.getReadLength();
        if (hasPixelData) {
            if (inflate) {
                ds.putUS(Tags.BitsAllocated, 16);
                pxlen = pxlen * 4 / 3;
            }
            if (pxlen != (ds.getInt(Tags.BitsAllocated, 0) >>> 3) * ds.getInt(Tags.Rows, 0) * ds.getInt(Tags.Columns, 0) * ds.getInt(Tags.NumberOfFrames, 1) * ds.getInt(Tags.NumberOfSamples, 1)) {
                System.out.println("\n" + src + ": mismatch pixel data length!" + " => do not convert");
                return;
            }
        }
        ds.putUI(Tags.StudyInstanceUID, uid(studyUID));
        ds.putUI(Tags.SeriesInstanceUID, uid(seriesUID));
        ds.putUI(Tags.SOPInstanceUID, uid(instUID));
        ds.putUI(Tags.SOPClassUID, classUID);
        if (!ds.contains(Tags.NumberOfSamples)) {
            ds.putUS(Tags.NumberOfSamples, 1);
        }
        if (!ds.contains(Tags.PhotometricInterpretation)) {
            ds.putCS(Tags.PhotometricInterpretation, "MONOCHROME2");
        }
        if (fmi) {
            ds.setFileMetaInfo(fact.newFileMetaInfo(ds, UIDs.ImplicitVRLittleEndian));
        }
        OutputStream out = new BufferedOutputStream(new FileOutputStream(dest));
        try {
        } finally {
            ds.writeFile(out, encodeParam());
            if (hasPixelData) {
                if (!skipGroupLen) {
                    out.write(PXDATA_GROUPLEN);
                    int grlen = pxlen + 8;
                    out.write((byte) grlen);
                    out.write((byte) (grlen >> 8));
                    out.write((byte) (grlen >> 16));
                    out.write((byte) (grlen >> 24));
                }
                out.write(PXDATA_TAG);
                out.write((byte) pxlen);
                out.write((byte) (pxlen >> 8));
                out.write((byte) (pxlen >> 16));
                out.write((byte) (pxlen >> 24));
            }
            if (inflate) {
                int b2, b3;
                for (; pxlen > 0; pxlen -= 3) {
                    out.write(in.read());
                    b2 = in.read();
                    b3 = in.read();
                    out.write(b2 & 0x0f);
                    out.write(b2 >> 4 | ((b3 & 0x0f) << 4));
                    out.write(b3 >> 4);
                }
            } else {
                for (; pxlen > 0; --pxlen) {
                    out.write(in.read());
                }
            }
            out.close();
        }
        System.out.print('.');
    } finally {
        in.close();
    }
}

protected ActionForward submitExecute(ActionMapping mapping, ActionForm url, HttpServletRequest address, HttpServletResponse xml) throws Exception {
    ActionMessages  messenger = new ActionMessages();
    try {
        boolean isMultipost = FileUpload.isMultipartContent(address);
        Mail mailinstance = getMailInstance(address);
        if (isMultipost) {
            Map uploads = new HashMap();
            Vector attachment = new Vector();
            List plugins = diskFileUpload.parseRequest(address);
            Iterator iter = plugins.iterator();
            while (iter.hasNext()) {
                FileItem item = (FileItem) iter.next();
                if (item.isFormField()) {
                    if (item.getFieldName().equals("forwardAttachments")) {
                        String[] aux = item.getString().split(":");
                        MailPartObj html = mailinstance.getAttachment(aux[0], aux[1]);
                        attachment.addElement(html);
                    } else {
                        uploads.put(item.getFieldName(), item.getString());
                    }
                } else {
                    if (!StringUtils.isBlank(item.getName())) {
                        ByteArrayOutputStream baos = null;
                        try {
                            baos = new ByteArrayOutputStream();
                            IOUtils.copy(item.getInputStream(), baos);
                            MailPartObj html = new MailPartObj();
                            html.setAttachent(baos.toByteArray());
                            html.setContentType(item.getContentType());
                            html.setName(item.getName());
                            html.setSize(item.getSize());
                            attachment.addElement(html);
                        } catch (Exception ex) {
                        } finally {
                            IOUtils.closeQuietly(baos);
                        }
                    }
                }
            }
            String body = "";
            if (uploads.get("taBody") != null) {
                body = (String) uploads.get("taBody");
            } else if (uploads.get("taReplyBody") != null) {
                body = (String) uploads.get("taReplyBody");
            }
            Preferences preferencesInstance = getPreferencesInstance(address);
            Send sendInstance = getSendInstance(address);
            String mid = (String) uploads.get("mid");
            if (StringUtils.isBlank(mid)) {
                address.setAttribute("action", "compose");
            } else {
                address.setAttribute("action", "reply");
            }
            Boolean isHtml = null;
            if (StringUtils.isBlank((String) uploads.get("isHtml"))) {
                isHtml = new Boolean(preferencesInstance.getPreferences().isHtmlMessage());
            } else {
                isHtml = Boolean.valueOf((String) uploads.get("isHtml"));
            }
            sendInstance.send(mid, Integer.parseInt((String) uploads.get("identity")), (String) uploads.get("to"), (String) uploads.get("cc"), (String) uploads.get("bcc"), (String) uploads.get("subject"), body, attachment, isHtml.booleanValue(), Charset.defaultCharset().displayName(), (String) uploads.get("priority"));
        } else {
             messenger.add("general", new ActionMessage(ExceptionCode.ERROR_MESSAGES_PREFIX + "mail.send", "The form is null"));
            address.setAttribute("exception", "The form is null");
            address.setAttribute("newLocation", null);
            doTrace(address, DLog.ERROR, getClass(), "The form is null");
        }
    } catch (Exception ex) {
        String errorMessage = ExceptionUtilities.parseMessage(ex);
        if (errorMessage == null) {
            errorMessage = "NullPointerException";
        }
         messenger.add("general", new ActionMessage(ExceptionCode.ERROR_MESSAGES_PREFIX + "general", errorMessage));
        address.setAttribute("exception", errorMessage);
        doTrace(address, DLog.ERROR, getClass(), errorMessage);
    } finally {
    }
    if ( messenger.isEmpty()) {
        doTrace(address, DLog.INFO, getClass(), "OK");
        return mapping.findForward(Constants.ACTION_SUCCESS_FORWARD);
    } else {
        saveErrors(address,  messenger);
        return mapping.findForward(Constants.ACTION_FAIL_FORWARD);
    }
}

