public static void copyFile(String fromPath, String toPath) {
    try {
        File inputFile = new File(fromPath);
        String dirImg = (new File(toPath)).getParent();
        File tmp = new File(dirImg);
        if (!tmp.exists()) {
            tmp.mkdir();
        }
        File outputFile = new File(toPath);
        if (!inputFile.getCanonicalPath().equals(outputFile.getCanonicalPath())) {
            FileInputStream in = new FileInputStream(inputFile);
            FileOutputStream out = new FileOutputStream(outputFile);
            int c;
            while ((c = in.read()) != -1) out.write(c);
            in.close();
            out.close();
        }
    } catch (Exception ex) {
        ex.printStackTrace();
        LogHandler.log(ex.getMessage(), Level.INFO, "LOG_MSG", isLoggingEnabled());
    }
}

private String sendPost(String sourceURL, String  urlString) {
    URL url;
    HttpURLConnection handler = null;
    try {
        url = new URL(sourceURL);
        handler = (HttpURLConnection) url.openConnection();
        handler.setRequestMethod("POST");
        handler.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");
        handler.setRequestProperty("Content-Length", "" + Integer.toString( urlString.getBytes().length));
        handler.setRequestProperty("Content-Language", "en-US");
        handler.setUseCaches(false);
        handler.setDoInput(true);
        handler.setDoOutput(true);
        DataOutputStream fr = new DataOutputStream(handler.getOutputStream());
        fr.writeBytes( urlString);
        fr.flush();
        fr.close();
        InputStream io = handler.getInputStream();
        BufferedReader rn = new BufferedReader(new InputStreamReader(io));
        String line;
        StringBuffer response = new StringBuffer();
        while ((line = rn.readLine()) != null) {
            response.append(line);
            response.append('\r');
        }
        rn.close();
        return response.toString();
    } catch (Exception e) {
        e.printStackTrace();
        return null;
    } finally {
        if (handler != null) {
            handler.disconnect();
        }
    }
}

