public static void doVersionCheck(View view) {
    view.showWaitCursor();
    try {
        URL url = new URL(jEdit.getProperty("version-check.url"));
        InputStream in = url.openStream();
        BufferedReader bin = new BufferedReader(new InputStreamReader(in));
        String line;
        String version = null;
        String build = null;
        while ((line = bin.readLine()) != null) {
            if (line.startsWith(".version")) version = line.substring(8).trim(); else if (line.startsWith(".build")) build = line.substring(6).trim();
        }
        bin.close();
        if (version != null && build != null) {
            if (jEdit.getBuild().compareTo(build) < 0) newVersionAvailable(view, version, url); else {
                GUIUtilities.message(view, "version-check" + ".up-to-date", new String[0]);
            }
        }
    } catch (IOException e) {
        String[] args = { jEdit.getProperty("version-check.url"), e.toString() };
        GUIUtilities.error(view, "read-error", args);
    }
    view.hideWaitCursor();
}

public void sync() throws IOException {
    JarOutputStream JarOutput = new JarOutputStream(outputStream, manifest);
    if (includeJars != null) {
        HashSet allAddry = new HashSet(includeJars);
        if (!ignoreDependencies) expandSet(allAddry);
        for (Iterator uri = allAddry.uri(); uri.hasNext(); ) {
            JarFile url = getJarFile(uri.next());
            Enumeration  jarEntories = url.entries();
            while ( jarEntories.hasMoreElements()) {
                ZipEntry po4 = (ZipEntry)  jarEntories.nextElement();
                if (po4.getName().equalsIgnoreCase("META-INF/MANIFEST.MF") || po4.getSize() <= 0) continue;
                JarOutput.putNextEntry(po4);
                InputStream connectionSteam = url.getInputStream(po4);
                IOUtils.copy(connectionSteam, JarOutput);
                JarOutput.closeEntry();
            }
        }
    }
    JarOutput.finish();
    JarOutput.close();
}

