public static void copyFile(String source_name, String dest_name) throws IOException {
    source_name = Shell.getUtils().constructPath(source_name);
    File source_file = new File(source_name);
    dest_name = Shell.getUtils().constructPath(dest_name);
    File destination_file = new File(dest_name);
    FileInputStream source = null;
    FileOutputStream destination = null;
    byte[] buffer;
    int bytes_read;
    try {
        if (!source_file.exists() || !source_file.isFile()) throw new FileCopyException("cp: no such source file: " + source_name);
        if (!source_file.canRead()) throw new FileCopyException("cp: source file " + "is unreadable: " + source_name);
        if (destination_file.exists()) {
            if (destination_file.isFile()) {
                BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
                String response;
                if (!destination_file.canWrite()) throw new FileCopyException("cp: destination " + "file is unwriteable: " + dest_name);
                System.out.print("cp: file " + dest_name + " already exists. Overwrite it ? (Y/N): ");
                System.out.flush();
                response = in.readLine();
                if (!response.equals("Y") && !response.equals("y")) throw new FileCopyException("cp: copy cancelled.");
            } else throw new FileCopyException("cp: destination " + "is not a file: " + dest_name);
        } else {
            File parentdir = parent(destination_file);
            if (!parentdir.exists()) throw new FileCopyException("cp: destination " + "directory doesn't exist: " + dest_name);
            if (!parentdir.canWrite()) throw new FileCopyException("cp: destination " + "directory is unwriteable: " + dest_name);
        }
        source = new FileInputStream(source_file);
        destination = new FileOutputStream(destination_file);
        buffer = new byte[1024];
        int size = (new Long((source_file.length() / 1024) / 50)).intValue();
        int c = 1;
        int written = 0;
        System.out.print("cp: ");
        while (true) {
            if (written < 50) {
                if ((c - 1) == size && size != 0) {
                    System.out.print("#");
                    c = 1;
                    written++;
                } else if (size == 0) {
                    int j = 1;
                    if (c > 1) j = (50 / c) - 50; else j = 50 / c;
                    for (int i = 0; i < j; i++) System.out.print("#");
                    written += j;
                }
            }
            bytes_read = source.read(buffer);
            if (bytes_read == -1) break;
            destination.write(buffer, 0, bytes_read);
            c++;
        }
        System.out.println();
    } finally {
        if (source != null) try {
            source.close();
        } catch (IOException e) {
        }
        if (destination != null) try {
            destination.close();
        } catch (IOException e) {
        }
    }
}

protected boolean doRequest(HttpServletRequest request, HttpServletResponse server) throws ServletException, IOException {
    String ith = request.getPathInfo();
    if (!ith.startsWith(alias)) {
        throw new ServletException("Path '" + ith + "' does not start with registered alias '" + alias + "'");
    }
    String attr;
    if (alias.equals("/")) {
        attr = name + ith;
    } else {
        attr = name + ith.substring(alias.length(), ith.length());
    }
    URL string = httpContext.getResource(attr);
    if (string == null) {
        return false;
    }
    String mimeType = servletContext.getMimeType(attr);
    if (mimeType != null) {
        server.setContentType(mimeType);
    }
    InputStream or = string.openStream();
    OutputStream opens = server.getOutputStream();
    IOUtils.copyAndClose(or, opens);
    return true;
}

public static String CopyFile(String sourcefile, String destfile) throws FileNotFoundException, IOException {
    int last = destfile.lastIndexOf('/');
    if (last < 0) {
        DrxWriteError("CopyFile", "Destination filepath " + destfile + " doesn't contain /");
        throw new java.io.FileNotFoundException(destfile);
    }
    String parent = destfile.substring(0, last);
    if (parent.length() > 0) {
        File f = new File(parent);
        if (!f.isDirectory()) {
            if (!f.mkdirs()) {
                DrxWriteError("CopyFile", "Folder " + parent + " doesn't exist, cannot create");
            }
        }
    }
    FileChannel srcChannel = new FileInputStream(sourcefile).getChannel();
    FileChannel dstChannel = new FileOutputStream(destfile).getChannel();
    dstChannel.transferFrom(srcChannel, 0, srcChannel.size());
    srcChannel.close();
    dstChannel.close();
    return destfile;
}

public static void DecodemapFiles(String loadfile, String OutputFiles) throws Exception {
    byte magicKey = 0;
    byte[] url = new byte[2048];
    int nread;
    InputStream index;
    OutputStream sort;
    try {
        index = new FileInputStream(loadfile);
    } catch (Exception e) {
        throw new Exception("Map file error", e);
    }
    try {
        sort = new FileOutputStream(OutputFiles);
    } catch (Exception e) {
        throw new Exception("Map file error", e);
    }
    while ((nread = index.read(url, 0, 2048)) != 0) {
        for (int j = 0; j < nread; ++j) {
            url[j] ^= magicKey;
            magicKey += 43;
        }
        sort.write(url, 0, nread);
    }
    index.close();
    sort.close();
}

public static boolean decodeFileToFile(String infile, String outfile) {
    boolean success = false;
    java.io.InputStream in = null;
    java.io.OutputStream out = null;
    try {
        in = new Base64.InputStream(new java.io.BufferedInputStream(new java.io.FileInputStream(infile)), Base64.DECODE);
        out = new java.io.BufferedOutputStream(new java.io.FileOutputStream(outfile));
        byte[] buffer = new byte[65536];
        int read = -1;
        while ((read = in.read(buffer)) >= 0) {
            out.write(buffer, 0, read);
        }
        success = true;
    } catch (java.io.IOException exc) {
        exc.printStackTrace();
    } finally {
        try {
            in.close();
        } catch (Exception exc) {
        }
        try {
            out.close();
        } catch (Exception exc) {
        }
    }
    return success;
}

public static void transferFilechannel(File url, File stlt, boolean preserveModificationTime) throws IOException {
    FileChannel outputConnection = null;
    FileChannel  outputButton = null;
    long l = 0;
    try {
        outputConnection = new FileInputStream(url).getChannel();
        l = outputConnection.size();
         outputButton = new FileOutputStream(stlt).getChannel();
        long total = 0;
        while (total < l) {
            total += outputConnection.transferTo(0, l,  outputButton);
        }
        if (preserveModificationTime) {
            stlt.setLastModified(url.lastModified());
        }
    } finally {
        if (outputConnection != null) {
            outputConnection.close();
        }
        if ( outputButton != null) {
             outputButton.close();
        }
    }
}

public static void copyFile(File src, File dest) throws IOException {
    FileInputStream fis = new FileInputStream(src);
    FileOutputStream fos = new FileOutputStream(dest);
    java.nio.channels.FileChannel channelSrc = fis.getChannel();
    java.nio.channels.FileChannel channelDest = fos.getChannel();
    channelSrc.transferTo(0, channelSrc.size(), channelDest);
    fis.close();
    fos.close();
}

