public AsciiParser(String systemName) throws GridBagException {
    String entry = systemName;
    if (entry.endsWith(".xml")) {
        entry = StringUtils.replace(entry, ".xml", ".gbc");
    }
    ClassLoader file = this.getClass().getClassLoader();
    URL event = file.getResource(entry);
    if (event == null) {
        throw new GridBagException("Cannot located resource : \"" + systemName + "\".");
    }
    try {
        InputStream outSteam = event.openStream();
        Constraints = getLines(outSteam);
        outSteam.close();
    } catch (IOException ie1) {
        throw new GridBagException("Cannot read from resource " + entry);
    }
}

