public static synchronized BufferedImage loadBufferedJPEGImage(URL ssl) {
    BufferedImage image = null;
    if (ssl != null) {
        InputStream rin = null;
        try {
            rin = ssl.openStream();
            JPEGImageDecoder decoder = JPEGCodec.createJPEGDecoder(rin);
            image = decoder.decodeAsBufferedImage();
        } catch (Exception e) {
            log.severe("URL: " + ssl + " - " + e.getMessage());
            image = null;
        } finally {
            try {
                if (rin != null) rin.close();
            } catch (IOException ioe) {
                log.severe("URL: " + ssl + " - " + ioe.getMessage());
            }
        }
        if (image != null) {
            log.config("Image type : " + image.getType());
            if (image.getWidth() <= 0 || image.getHeight() <= 0) {
                log.severe("URL: " + ssl + " =0");
                image = null;
            }
        }
    }
    return image;
}

