static HttpURLConnection remote(String file, String call, String mediaType, String content, int timeoutMis) throws ProtocolException, IOException, MalformedURLException, UnsupportedEncodingException {
    HttpURLConnection conn = (HttpURLConnection) (new URL(file).openConnection());
    conn.setRequestMethod(call);
    conn.setConnectTimeout(timeoutMis);
    byte[] bContent = null;
    if (content != null && content.length() > 0) {
        conn.setDoOutput(true);
        conn.setRequestProperty("Content-Type", mediaType);
        bContent = content.getBytes("UTF-8");
        conn.setFixedLengthStreamingMode(bContent.length);
    }
    conn.remote();
    if (bContent != null) {
        OutputStream os = conn.getOutputStream();
        os.write(bContent);
        os.flush();
        os.close();
    }
    return conn;
}

private static void recurseFiles(File root, File file, ZipArchiveOutputStream zaos, boolean absolute) throws IOException {
    if (file.isDirectory()) {
        File[] files = file.listFiles();
        for (File file2 : files) {
            recurseFiles(root, file2, zaos, absolute);
        }
    } else if ((!file.getName().endsWith(".zip")) && (!file.getName().endsWith(".ZIP"))) {
        String filename = null;
        if (absolute) {
            filename = file.getAbsolutePath().substring(root.getAbsolutePath().length());
        } else {
            filename = file.getName();
        }
        ZipArchiveEntry zae = new ZipArchiveEntry(filename);
        zae.setSize(file.length());
        zaos.putArchiveEntry(zae);
        FileInputStream fis = new FileInputStream(file);
        IOUtils.copy(fis, zaos);
        zaos.closeArchiveEntry();
    }
}

public void send(org.hibernate.Session hsession, Session session, String repositoryName, int ideIdint, String to, String cc, String bcc, String subject, String body, Vector Attachings, boolean isHtml, String charset, InternetHeaders headers, String priority) throws MailException {
    try {
        if (charset == null) {
            charset = MimeUtility.javaCharset(Charset.defaultCharset().displayName());
        }
        if ((body == null) || body.trim().equals("")) {
            body = " ";
        }
        Email url = null;
        if (isHtml) {
            url = new HtmlEmail();
        } else {
            url = new MultiPartEmail();
        }
        url.setCharset(charset);
        Users user = getUser(hsession, repositoryName);
        Identity identity = getIdentity(hsession, ideIdint, user);
        InternetAddress _returnPath = new InternetAddress(identity.getIdeEmail(), identity.getIdeName());
        InternetAddress _from = new InternetAddress(identity.getIdeEmail(), identity.getIdeName());
        InternetAddress _replyTo = new InternetAddress(identity.getIdeReplyTo(), identity.getIdeName());
        InternetAddress[] _to = MessageUtilities.encodeAddresses(to, null);
        InternetAddress[] _cc = MessageUtilities.encodeAddresses(cc, null);
        InternetAddress[] _bcc = MessageUtilities.encodeAddresses(bcc, null);
        if (_from != null) {
            url.setFrom(_from.getAddress(), _from.getPersonal());
        }
        if (_returnPath != null) {
            url.addHeader("Return-Path", _returnPath.getAddress());
            url.addHeader("Errors-To", _returnPath.getAddress());
            url.addHeader("X-Errors-To", _returnPath.getAddress());
        }
        if (_replyTo != null) {
            url.addReplyTo(_replyTo.getAddress(), _replyTo.getPersonal());
        }
        if ((_to != null) && (_to.length > 0)) {
            HashSet aux = new HashSet(_to.length);
            Collections.addAll(aux, _to);
            url.setTo(aux);
        }
        if ((_cc != null) && (_cc.length > 0)) {
            HashSet aux = new HashSet(_cc.length);
            Collections.addAll(aux, _cc);
            url.setCc(aux);
        }
        if ((_bcc != null) && (_bcc.length > 0)) {
            HashSet aux = new HashSet(_bcc.length);
            Collections.addAll(aux, _bcc);
            url.setBcc(aux);
        }
        url.setSubject(subject);
        Date now = new Date();
        url.setSentDate(now);
        File dir = new File(System.getProperty("user.home") + File.separator + "tmp");
        if (!dir.exists()) {
            dir.mkdir();
        }
        if ((Attachings != null) && (Attachings.size() > 0)) {
            for (int i = 0; i < Attachings.size(); i++) {
                ByteArrayInputStream bais = null;
                FileOutputStream fos = null;
                try {
                    MailPartObj obj = (MailPartObj) Attachings.get(i);
                    File file = new File(dir, obj.getName());
                    bais = new ByteArrayInputStream(obj.getAttachent());
                    fos = new FileOutputStream(file);
                    IOUtils.copy(bais, fos);
                    EmailAttachment attachment = new EmailAttachment();
                    attachment.setPath(file.getPath());
                    attachment.setDisposition(EmailAttachment.ATTACHMENT);
                    attachment.setDescription("File Attachment: " + file.getName());
                    attachment.setName(file.getName());
                    if (url instanceof MultiPartEmail) {
                        ((MultiPartEmail) url).attach(attachment);
                    }
                } catch (Exception ex) {
                } finally {
                    IOUtils.closeQuietly(bais);
                    IOUtils.closeQuietly(fos);
                }
            }
        }
        String mid = getId();
        if (headers != null) {
            Header xheader;
            Enumeration xe = headers.getAllHeaders();
            for (; xe.hasMoreElements(); ) {
                xheader = (Header) xe.nextElement();
                if (xheader.getName().equals(RFC2822Headers.IN_REPLY_TO)) {
                    url.addHeader(xheader.getName(), xheader.getValue());
                } else if (xheader.getName().equals(RFC2822Headers.REFERENCES)) {
                    url.addHeader(xheader.getName(), xheader.getValue());
                }
            }
        } else {
            url.addHeader(RFC2822Headers.IN_REPLY_TO, "<" + mid + ".JavaMail.duroty@duroty" + ">");
            url.addHeader(RFC2822Headers.REFERENCES, "<" + mid + ".JavaMail.duroty@duroty" + ">");
        }
        if (priority != null) {
            if (priority.equals("high")) {
                url.addHeader("Importance", priority);
                url.addHeader("X-priority", "1");
            } else if (priority.equals("low")) {
                url.addHeader("Importance", priority);
                url.addHeader("X-priority", "5");
            }
        }
        if (url instanceof HtmlEmail) {
            ((HtmlEmail) url).setHtmlMsg(body);
        } else {
            url.setMsg(body);
        }
        url.setMailSession(session);
        url.buildMimeMessage();
        MimeMessage mime = url.getMimeMessage();
        int size = MessageUtilities.getMessageSize(mime);
        if (!controlQuota(hsession, user, size)) {
            throw new MailException("ErrorMessages.mail.quota.exceded");
        }
        messageable.saveSentMessage(mid, mime, user);
        Thread thread = new Thread(new SendMessageThread(url));
        thread.start();
    } catch (MailException e) {
        throw e;
    } catch (Exception e) {
        throw new MailException(e);
    } catch (java.lang.OutOfMemoryError ex) {
        System.gc();
        throw new MailException(ex);
    } catch (Throwable e) {
        throw new MailException(e);
    } finally {
        GeneralOperations.closeHibernateSession(hsession);
    }
}

private static void main(String[] args) {
    try {
        File f = new File("test.txt");
        if (f.exists()) {
            throw new IOException(f + " already exists.  I don't want to overwrite it.");
        }
        StraightStreamReader in;
        char[] cbuf = new char[0x1000];
        int read;
        int totRead;
        FileOutputStream out = new FileOutputStream(f);
        for (int i = 0x00; i < 0x100; i++) {
            out.write(i);
        }
        out.close();
        in = new StraightStreamReader(new FileInputStream(f));
        for (int i = 0x00; i < 0x100; i++) {
            read = in.read();
            if (read != i) {
                System.err.println("Error: " + i + " read as " + read);
            }
        }
        in.close();
        in = new StraightStreamReader(new FileInputStream(f));
        totRead = in.read(cbuf);
        if (totRead != 0x100) {
            System.err.println("Simple buffered read did not read the full amount: 0x" + Integer.toHexString(totRead));
        }
        for (int i = 0x00; i < totRead; i++) {
            if (cbuf[i] != i) {
                System.err.println("Error: 0x" + i + " read as 0x" + cbuf[i]);
            }
        }
        in.close();
        in = new StraightStreamReader(new FileInputStream(f));
        totRead = 0;
        while (totRead <= 0x100 && (read = in.read(cbuf, totRead, 0x100 - totRead)) > 0) {
            totRead += read;
        }
        if (totRead != 0x100) {
            System.err.println("Not enough read. Bytes read: " + Integer.toHexString(totRead));
        }
        for (int i = 0x00; i < totRead; i++) {
            if (cbuf[i] != i) {
                System.err.println("Error: 0x" + i + " read as 0x" + cbuf[i]);
            }
        }
        in.close();
        in = new StraightStreamReader(new FileInputStream(f));
        totRead = 0;
        while (totRead <= 0x100 && (read = in.read(cbuf, totRead + 0x123, 0x100 - totRead)) > 0) {
            totRead += read;
        }
        if (totRead != 0x100) {
            System.err.println("Not enough read. Bytes read: " + Integer.toHexString(totRead));
        }
        for (int i = 0x00; i < totRead; i++) {
            if (cbuf[i + 0x123] != i) {
                System.err.println("Error: 0x" + i + " read as 0x" + cbuf[i + 0x123]);
            }
        }
        in.close();
        in = new StraightStreamReader(new FileInputStream(f));
        totRead = 0;
        while (totRead <= 0x100 && (read = in.read(cbuf, totRead + 0x123, 7)) > 0) {
            totRead += read;
        }
        if (totRead != 0x100) {
            System.err.println("Not enough read. Bytes read: " + Integer.toHexString(totRead));
        }
        for (int i = 0x00; i < totRead; i++) {
            if (cbuf[i + 0x123] != i) {
                System.err.println("Error: 0x" + i + " read as 0x" + cbuf[i + 0x123]);
            }
        }
        in.close();
        f.delete();
    } catch (IOException x) {
        System.err.println(x.getMessage());
    }
}

private static boolean setBundleInfoName(String location, List<BundleInfo> list) {
    try {
        URL string = new URL(location);
        BufferedReader br = new BufferedReader(new InputStreamReader(string.openStream(), "UTF-8"));
        while (true) {
            String line = br.readLine();
            if (line == null) {
                break;
            }
            int pos1 = line.indexOf('=');
            if (pos1 < 0) {
                continue;
            }
            String bundleSymbolicName = line.substring(0, pos1);
            String bundleName = line.substring(pos1 + 1);
            for (BundleInfo info : list) {
                if (info.bundleSymbolicName.equals(bundleSymbolicName)) {
                    info.bundleName = bundleName;
                    break;
                }
            }
        }
        return true;
    } catch (IOException e) {
        e.printStackTrace();
    }
    return false;
}

public void convert(File src, File dest) throws IOException {
    InputStream in = new BufferedInputStream(new FileInputStream(src));
    DcmParser p = pfact.newDcmParser(in);
    Dataset ds = fact.newDataset();
    p.setDcmHandler(ds.getDcmHandler());
    try {
        FileFormat format = p.detectFileFormat();
        if (format != FileFormat.ACRNEMA_STREAM) {
            System.out.println("\n" + src + ": not an ACRNEMA stream!");
            return;
        }
        p.parseDcmFile(format, Tags.PixelData);
        if (ds.contains(Tags.StudyInstanceUID) || ds.contains(Tags.SeriesInstanceUID) || ds.contains(Tags.SOPInstanceUID) || ds.contains(Tags.SOPClassUID)) {
            System.out.println("\n" + src + ": contains UIDs!" + " => probable already DICOM - do not convert");
            return;
        }
        boolean hasPixelData = p.getReadTag() == Tags.PixelData;
        boolean inflate = hasPixelData && ds.getInt(Tags.BitsAllocated, 0) == 12;
        int pxlen = p.getReadLength();
        if (hasPixelData) {
            if (inflate) {
                ds.putUS(Tags.BitsAllocated, 16);
                pxlen = pxlen * 4 / 3;
            }
            if (pxlen != (ds.getInt(Tags.BitsAllocated, 0) >>> 3) * ds.getInt(Tags.Rows, 0) * ds.getInt(Tags.Columns, 0) * ds.getInt(Tags.NumberOfFrames, 1) * ds.getInt(Tags.NumberOfSamples, 1)) {
                System.out.println("\n" + src + ": mismatch pixel data length!" + " => do not convert");
                return;
            }
        }
        ds.putUI(Tags.StudyInstanceUID, uid(studyUID));
        ds.putUI(Tags.SeriesInstanceUID, uid(seriesUID));
        ds.putUI(Tags.SOPInstanceUID, uid(instUID));
        ds.putUI(Tags.SOPClassUID, classUID);
        if (!ds.contains(Tags.NumberOfSamples)) {
            ds.putUS(Tags.NumberOfSamples, 1);
        }
        if (!ds.contains(Tags.PhotometricInterpretation)) {
            ds.putCS(Tags.PhotometricInterpretation, "MONOCHROME2");
        }
        if (fmi) {
            ds.setFileMetaInfo(fact.newFileMetaInfo(ds, UIDs.ImplicitVRLittleEndian));
        }
        OutputStream out = new BufferedOutputStream(new FileOutputStream(dest));
        try {
        } finally {
            ds.writeFile(out, encodeParam());
            if (hasPixelData) {
                if (!skipGroupLen) {
                    out.write(PXDATA_GROUPLEN);
                    int grlen = pxlen + 8;
                    out.write((byte) grlen);
                    out.write((byte) (grlen >> 8));
                    out.write((byte) (grlen >> 16));
                    out.write((byte) (grlen >> 24));
                }
                out.write(PXDATA_TAG);
                out.write((byte) pxlen);
                out.write((byte) (pxlen >> 8));
                out.write((byte) (pxlen >> 16));
                out.write((byte) (pxlen >> 24));
            }
            if (inflate) {
                int b2, b3;
                for (; pxlen > 0; pxlen -= 3) {
                    out.write(in.read());
                    b2 = in.read();
                    b3 = in.read();
                    out.write(b2 & 0x0f);
                    out.write(b2 >> 4 | ((b3 & 0x0f) << 4));
                    out.write(b3 >> 4);
                }
            } else {
                for (; pxlen > 0; --pxlen) {
                    out.write(in.read());
                }
            }
            out.close();
        }
        System.out.print('.');
    } finally {
        in.close();
    }
}

