public Document page() throws CrawlingException {
    log.debug("BEGINIG indexing page [code=" + getCode() + "] ...");
    URL impl = null;
    InputStream nin = null;
    String contentType = null;
    try {
        impl = new URL(getServer().getProtocol() + "://" + getServer().getHost() + ":" + getServer().getPort() + getPath());
        HttpURLConnection pageContent = (HttpURLConnection) impl.openConnection();
        if (pageContent.getResponseCode() != HttpURLConnection.HTTP_OK) {
            log.debug("page pk[" + getCode() + "," + impl.toExternalForm() + "] is invalid");
            return null;
        }
        String redireccion = pageContent.getHeaderField("location");
        if (redireccion != null) {
            log.debug("Page " + impl.toExternalForm() + " redirected to " + redireccion);
            recordLink(redireccion);
            return null;
        }
        contentType = pageContent.getContentType();
        nin = new BufferedInputStream(pageContent.getInputStream(), 32768);
    } catch (MalformedURLException e) {
        log.error("Invalid page address", e);
    } catch (ConnectException e) {
        if (getServer() != null) {
            log.error("Unable to connect to page: " + getServer().getProtocol() + "://" + getServer().getHost() + ":" + getServer().getPort() + getPath(), e);
        }
    } catch (UnknownHostException uhe) {
        log.warn("Unknow host indexing page " + getURL(), uhe);
    } catch (IOException e) {
        log.warn("Unable to index page " + getURL(), e);
    }
    Document doc = generateDocument(contentType, nin);
    log.debug("END indexing page [code=" + getCode() + "]");
    return doc;
}

private static boolean copyFile(File in, File out) {
    boolean ok = true;
    InputStream is = null;
    OutputStream os = null;
    try {
        is = new FileInputStream(in);
        os = new FileOutputStream(out);
        byte[] buffer = new byte[0xFFFF];
        for (int len; (len = is.read(buffer)) != -1; ) os.write(buffer, 0, len);
    } catch (IOException e) {
        System.err.println(e);
        ok = false;
    } finally {
        if (is != null) {
            try {
                is.close();
            } catch (IOException e) {
                System.err.println(e);
            }
        }
        if (os != null) {
            try {
                os.close();
            } catch (IOException e) {
                System.err.println(e);
            }
        }
    }
    return ok;
}

private void handleNoderegainsEvent(long issueName, long nodeRI, String ipAddr, long serviceIDs, String EventTim) {
    Category url = ThreadCategory.getInstance(OutageWriter.class);
    if (issueName == -1 || nodeRI == -1 || ipAddr == null || serviceIDs == -1) {
        url.warn(EventConstants.NODE_REGAINED_SERVICE_EVENT_UEI + " ignored - info incomplete - eventid/nodeid/ip/svc: " + issueName + "/" + nodeRI + "/" + ipAddr + "/" + serviceIDs);
        return;
    }
    Connection logDb = null;
    try {
        logDb = DatabaseConnectionFactory.getInstance().getConnection();
        if (openOutageExists(logDb, nodeRI, ipAddr, serviceIDs)) {
            try {
                logDb.setAutoCommit(false);
            } catch (SQLException sqle) {
                url.error("Unable to change database AutoCommit to FALSE", sqle);
                return;
            }
            PreparedStatement outageUpdarter = logDb.prepareStatement(OutageConstants.DB_UPDATE_OUTAGE_FOR_SERVICE);
            outageUpdarter.setLong(1, issueName);
            outageUpdarter.setTimestamp(2, convertEventTimeIntoTimestamp(EventTim));
            outageUpdarter.setLong(3, nodeRI);
            outageUpdarter.setString(4, ipAddr);
            outageUpdarter.setLong(5, serviceIDs);
            outageUpdarter.executeUpdate();
            outageUpdarter.close();
            try {
                logDb.commit();
                if (url.isDebugEnabled()) url.debug("nodeRegainedService: closed outage for nodeid/ip/service " + nodeRI + "/" + ipAddr + "/" + serviceIDs + " in DB");
            } catch (SQLException se) {
                url.warn("Rolling back transaction, nodeRegainedService could not be recorded  for nodeId/ipAddr/service: " + nodeRI + "/" + ipAddr + "/" + serviceIDs, se);
                try {
                    logDb.rollback();
                } catch (SQLException sqle) {
                    url.warn("SQL exception during rollback, reason", sqle);
                }
            }
        } else {
            url.warn("\'" + EventConstants.NODE_REGAINED_SERVICE_EVENT_UEI + "\' for " + nodeRI + "/" + ipAddr + "/" + serviceIDs + " does not have open record.");
        }
    } catch (SQLException se) {
        url.warn("SQL exception while handling \'nodeRegainedService\'", se);
    } finally {
        try {
            if (logDb != null) logDb.close();
        } catch (SQLException e) {
            url.warn("Exception closing JDBC connection", e);
        }
    }
}

public void testTransactions() throws Exception {
    con = TestUtil.openDB();
    Statement st;
    ResultSet rs;
    con.setAutoCommit(false);
    assertTrue(!con.getAutoCommit());
    con.setAutoCommit(true);
    assertTrue(con.getAutoCommit());
    st = con.createStatement();
    st.executeUpdate("insert into test_a (imagename,image,id) values ('comttest',1234,5678)");
    con.setAutoCommit(false);
    st.executeUpdate("update test_a set image=9876 where id=5678");
    con.commit();
    rs = st.executeQuery("select image from test_a where id=5678");
    assertTrue(rs.next());
    assertEquals(9876, rs.getInt(1));
    rs.close();
    st.executeUpdate("update test_a set image=1111 where id=5678");
    con.rollback();
    rs = st.executeQuery("select image from test_a where id=5678");
    assertTrue(rs.next());
    assertEquals(9876, rs.getInt(1));
    rs.close();
    TestUtil.closeDB(con);
}

private void addQDInformation() {
    try {
        long qdDate = System.currentTimeMillis();
        if (_local == true) {
            File qdFile = new File("qdinfo.dat");
            if (!qdFile.exists()) {
                return;
            }
            qdDate = qdFile.lastModified();
        }
        if (qdDate > this._qdFileDate) {
            this._qdFileDate = qdDate;
            for (int ii = 0; ii < this._projectInfo.size(); ii++) {
                Information info = getInfo(ii);
                if (info != null) {
                    info._qdValue = null;
                }
            }
            Reader reader = null;
            if (_local == true) {
                reader = new FileReader("qdinfo.dat");
            } else {
                StringBuffer webInfo = new StringBuffer();
                webInfo.append("http://boston.quik.com/rph/");
                webInfo.append("qdinfo.dat");
                try {
                    URL url = new URL(webInfo.toString());
                    InputStream stream = url.openStream();
                    reader = new InputStreamReader(stream);
                } catch (MalformedURLException mue) {
                    mue.printStackTrace();
                }
            }
            BufferedReader file = new BufferedReader(reader);
            try {
                String line = null;
                while ((line = file.readLine()) != null) {
                    if (line.startsWith("pg ")) {
                        this._qdDate = Long.parseLong(line.substring(3), 16);
                        this._qdDate = (this._qdDate + 946684800) * 1000;
                    } else if (line.startsWith("pt ")) {
                        line = line.substring(3).trim();
                        int pos = -1;
                        while ((line.length() > 0) && ((pos = line.indexOf(' ')) > 0)) {
                            int projectNum = 0;
                            Double value = null;
                            if (pos > 0) {
                                projectNum = Integer.parseInt(line.substring(0, pos));
                                line = line.substring(pos).trim();
                            }
                            pos = line.indexOf(' ');
                            if (pos > 0) {
                                value = new Double((double) Integer.parseInt(line.substring(0, pos)) / 100);
                                line = line.substring(pos).trim();
                            }
                            Information info = getInfo(projectNum);
                            if (info == null) {
                                info = createInfo(projectNum);
                            }
                            if (info._qdValue == null) {
                                info._qdValue = value;
                            }
                        }
                    }
                }
            } finally {
                file.close();
            }
        }
    } catch (FileNotFoundException e) {
    } catch (IOException e) {
    }
}

public static boolean isCodebaseDownloadable(Properties p) {
    class CodebaseData {

        String file;

        boolean success = true;
    }
    String codebase = p.getProperty("java.rmi.server.codebase", null);
    if (null == codebase) {
        if (logger.isDebugEnabled()) logger.debug("java.rmi.server.codebase = null (return false)");
        return false;
    }
    try {
        URL cbUrl = new URL(codebase);
        String protocol = cbUrl.getProtocol();
        String filename = cbUrl.getFile();
        if (logger.isDebugEnabled()) {
            logger.debug("Verifying java.rmi.server.codebase setting(s)...");
            logger.debug("Codebase = " + cbUrl.toString());
        }
        if (protocol.equals("http")) {
            if (filename.indexOf("http") == -1) {
                try {
                    int size = cbUrl.openConnection().getContentLength();
                    if (logger.isDebugEnabled()) logger.debug("Checking " + cbUrl + " : OK");
                    return true;
                } catch (IOException e) {
                    if (logger.isDebugEnabled()) logger.debug("Checking " + cbUrl + " : FAIL");
                    return false;
                } finally {
                    if (logger.isDebugEnabled()) logger.debug("Verifying java.rmi.server.codebase setting(s)... Done!");
                }
            } else {
                ArrayList files = new ArrayList();
                StringTokenizer st = new StringTokenizer(codebase);
                URL url = null;
                String part = null;
                CodebaseData data = null;
                while (st.hasMoreTokens()) {
                    part = st.nextToken();
                    url = new URL(part);
                    data = new CodebaseData();
                    try {
                        int len = url.openConnection().getContentLength();
                        if (len == -1) {
                            data.success = false;
                            data.file = part;
                        } else {
                            data.file = part;
                        }
                    } catch (IOException e) {
                        data.success = false;
                    }
                    files.add(data);
                }
                String wrong = null;
                CodebaseData codebaseData = null;
                boolean allOK = true;
                int errorFiles = 0;
                for (int i = 0; i < files.size(); i++) {
                    codebaseData = (CodebaseData) files.get(i);
                    if (!codebaseData.success) {
                        wrong += " " + codebaseData.file;
                        ++errorFiles;
                        allOK = false;
                    }
                    if (logger.isDebugEnabled()) logger.debug((i + 1) + ". Checking " + codebaseData.file + " : " + (codebaseData.success ? "OK" : "FAIL"));
                }
                if (errorFiles == 0) {
                    if (logger.isDebugEnabled()) logger.debug("All entries can be downloaded successfully!");
                } else {
                    logger.error(errorFiles + " of " + files.size() + " entries can *not* be downloaded successfully!");
                }
                if (logger.isDebugEnabled()) logger.debug("Verifying java.rmi.server.codebase setting(s)... Done!");
                if (allOK) {
                    return true;
                } else {
                    return false;
                }
            }
        } else if (protocol.equalsIgnoreCase("file")) {
            if (logger.isDebugEnabled()) logger.debug("'file' protocol not supported for JSF");
        }
    } catch (MalformedURLException e) {
        logger.debug(e.getMessage());
        return false;
    }
    return false;
}

protected ActionForward submitExecute(ActionMapping  matching, ActionForm url, HttpServletRequest input, HttpServletResponse xml) throws Exception {
    ActionMessages  messenger = new ActionMessages();
    try {
        boolean isMultompost = FileUpload.isMultipartContent(input);
        Mail mailinstance = getMailInstance(input);
        if (isMultompost) {
            Map uploads = new HashMap();
            Vector attachment = new Vector();
            List plugins = diskFileUpload.parseRequest(input);
            Iterator iter = plugins.iterator();
            while (iter.hasNext()) {
                FileItem item = (FileItem) iter.next();
                if (item.isFormField()) {
                    if (item.getFieldName().equals("forwardAttachments")) {
                        String[] aux = item.getString().split(":");
                        MailPartObj html = mailinstance.getAttachment(aux[0], aux[1]);
                        attachment.addElement(html);
                    } else {
                        uploads.put(item.getFieldName(), item.getString());
                    }
                } else {
                    if (!StringUtils.isBlank(item.getName())) {
                        ByteArrayOutputStream baos = null;
                        try {
                            baos = new ByteArrayOutputStream();
                            IOUtils.copy(item.getInputStream(), baos);
                            MailPartObj html = new MailPartObj();
                            html.setAttachent(baos.toByteArray());
                            html.setContentType(item.getContentType());
                            html.setName(item.getName());
                            html.setSize(item.getSize());
                            attachment.addElement(html);
                        } catch (Exception ex) {
                        } finally {
                            IOUtils.closeQuietly(baos);
                        }
                    }
                }
            }
            String body = "";
            if (uploads.get("taBody") != null) {
                body = (String) uploads.get("taBody");
            } else if (uploads.get("taReplyBody") != null) {
                body = (String) uploads.get("taReplyBody");
            }
            Preferences preferencesInstance = getPreferencesInstance(input);
            Send sendInstance = getSendInstance(input);
            String mid = (String) uploads.get("mid");
            if (StringUtils.isBlank(mid)) {
                input.setAttribute("action", "compose");
            } else {
                input.setAttribute("action", "reply");
            }
            Boolean isHtml = null;
            if (StringUtils.isBlank((String) uploads.get("isHtml"))) {
                isHtml = new Boolean(preferencesInstance.getPreferences().isHtmlMessage());
            } else {
                isHtml = Boolean.valueOf((String) uploads.get("isHtml"));
            }
            sendInstance.send(mid, Integer.parseInt((String) uploads.get("identity")), (String) uploads.get("to"), (String) uploads.get("cc"), (String) uploads.get("bcc"), (String) uploads.get("subject"), body, attachment, isHtml.booleanValue(), Charset.defaultCharset().displayName(), (String) uploads.get("priority"));
        } else {
             messenger.add("general", new ActionMessage(ExceptionCode.ERROR_MESSAGES_PREFIX + "mail.send", "The form is null"));
            input.setAttribute("exception", "The form is null");
            input.setAttribute("newLocation", null);
            doTrace(input, DLog.ERROR, getClass(), "The form is null");
        }
    } catch (Exception ex) {
        String errorMessage = ExceptionUtilities.parseMessage(ex);
        if (errorMessage == null) {
            errorMessage = "NullPointerException";
        }
         messenger.add("general", new ActionMessage(ExceptionCode.ERROR_MESSAGES_PREFIX + "general", errorMessage));
        input.setAttribute("exception", errorMessage);
        doTrace(input, DLog.ERROR, getClass(), errorMessage);
    } finally {
    }
    if ( messenger.isEmpty()) {
        doTrace(input, DLog.INFO, getClass(), "OK");
        return  matching.findForward(Constants.ACTION_SUCCESS_FORWARD);
    } else {
        saveErrors(input,  messenger);
        return  matching.findForward(Constants.ACTION_FAIL_FORWARD);
    }
}

private File extractUninstallFiles(File _destPath, boolean upgrade, String lastVer) {
    File oldlog = null;
    try {
        boolean oldClassCopied = false;
        File destPath = new File(_destPath, "vai_" + VAGlobals.APP_NAME + "_" + VAGlobals.APP_VERSION);
        if (upgrade) {
            File lastVerPath = new File(_destPath, "vai_" + VAGlobals.APP_NAME + "_" + lastVer);
            if (destPath.equals(lastVerPath)) {
                File bkdir = new File(destPath.getAbsolutePath() + ".bak");
                if (!destPath.renameTo(bkdir)) {
                    throw new IOException(VAGlobals.i18n("Setup_NotCreateDirectory") + " " + destPath);
                }
                oldlog = new File(bkdir.getAbsolutePath() + System.getProperty("file.separator") + "uninstall.vai");
                lastVerPath = bkdir;
            } else {
                oldlog = new File(lastVerPath.getAbsolutePath() + System.getProperty("file.separator") + "uninstall.vai");
            }
            if ((!destPath.exists()) && (!destPath.mkdirs())) {
                throw new IOException(VAGlobals.i18n("Setup_NotCreateDirectory") + " " + destPath);
            }
            if (uInfo_.module) oldClassCopied = copyOldSetupClass(lastVerPath, destPath);
        } else {
            if ((!destPath.exists()) && (!destPath.mkdirs())) {
                throw new IOException(VAGlobals.i18n("Setup_NotCreateDirectory") + " " + destPath);
            }
        }
        dirty_ = true;
        File[] ls = destPath.listFiles();
        for (int i = 0; i < ls.length; i++) {
            if (!oldClassCopied) ls[i].delete(); else if (!ls[i].getPath().equals(destPath.getAbsolutePath() + File.separator + installClassName_ + ".class")) ls[i].delete();
        }
        byte[] buf = new byte[0];
        int read = 0;
        if (!oldClassCopied && (installClassSize_ > 0 || jarOffset_ > 0)) {
            final File outClassFile = new File(destPath.getAbsolutePath() + File.separator + installClassName_ + ".class");
            if (outClassFile.exists() && !outClassFile.delete()) {
                ui_.showError(new Exception(VAGlobals.i18n("Setup_FileNotCreated") + ":\n" + outClassFile.getName()));
            }
            final FileOutputStream out = new FileOutputStream(outClassFile);
            final FileInputStream in = new FileInputStream(fileWithArchive_);
            if (installClassOffset_ > 0) {
                in.skip(installClassOffset_);
            }
            buf = new byte[0];
            if (installClassSize_ < 0) buf = new byte[(int) jarOffset_]; else buf = new byte[(int) installClassSize_];
            read = in.read(buf, 0, buf.length);
            out.write(buf, 0, read);
            out.close();
            in.close();
        }
        final FileInputStream in = new FileInputStream(fileWithArchive_);
        if (jarOffset_ > 0) {
            in.skip(jarOffset_);
        }
        JarInputStream jar = new JarInputStream(in);
        final File outJarFile = new File(destPath.getAbsolutePath() + File.separator + "install.jar");
        if (outJarFile.exists() && !outJarFile.delete()) {
            ui_.showError(new Exception(VAGlobals.i18n("Setup_FileNotCreated") + ":\n" + outJarFile.getName()));
        }
        JarOutputStream outJar = new JarOutputStream(new FileOutputStream(outJarFile));
        ZipEntry entry = jar.getNextEntry();
        final int bufSize = 32768;
        buf = new byte[bufSize];
        while (entry != null) {
            String entryName = entry.getName();
            if (entryName.equals("com/memoire/vainstall/resources/vainstall.properties")) {
            } else if (entryName.equals(installClassName_ + ".class") && !oldClassCopied) {
                FileOutputStream out = null;
                try {
                    out = new FileOutputStream(destPath.getAbsolutePath() + File.separator + installClassName_ + ".class");
                    VAGlobals.copyStream(jar, out, buf);
                } catch (IOException e) {
                    throw e;
                } finally {
                    if (out != null) out.close();
                }
            } else if (!entryName.endsWith(".zip")) {
                if (VAGlobals.DEBUG) VAGlobals.printDebug("jar entry name " + entryName);
                ZipEntry outEntry = new ZipEntry(entryName);
                CRC32 crc = new CRC32();
                outJar.putNextEntry(outEntry);
                int size = 0;
                while ((read = jar.read(buf, 0, bufSize)) >= 0) {
                    size += read;
                    if (read == 0) {
                        Thread.yield();
                    } else {
                        outJar.write(buf, 0, read);
                        crc.update(buf, 0, read);
                    }
                }
                outEntry.setSize(size);
                outEntry.setCrc(crc.getValue());
                outJar.flush();
                outJar.closeEntry();
            }
            jar.closeEntry();
            entry = jar.getNextEntry();
        }
        InputStream pin = getClass().getResourceAsStream("resources/vainstall.properties");
        Properties prop = new Properties();
        try {
            prop.load(pin);
        } catch (IOException exc) {
        }
        if (language == null) language = "default";
        prop.setProperty("vainstall.destination.language", language);
        ZipEntry outEntry = new ZipEntry("com/memoire/vainstall/resources/vainstall.properties");
        CRC32 crc = new CRC32();
        outEntry.setCrc(crc.getValue());
        outEntry.setSize(prop.size());
        outJar.putNextEntry(outEntry);
        prop.store(outJar, VAGlobals.NAME + " " + VAGlobals.VERSION);
        outEntry.setCrc(crc.getValue());
        outJar.closeEntry();
        jar.close();
        outJar.close();
        in.close();
    } catch (IOException e) {
        String message = e.getLocalizedMessage();
        message += "\n" + VAGlobals.i18n("Setup_ErrorUninstallScripts");
        e.printStackTrace();
        exitOnError(new IOException(message));
    }
    return oldlog;
}

