        if (inChannel != null) inChannel.close();
        if (outChannel != null) outChannel.close();
    }
}

protected String issueCommandToServer(String buffer, ChangeCapsule changeCapsule) throws IOException {
    URLConnection serverConn = serverURL.openConnection();
    serverConn.setDoInput(true);
    serverConn.setDoOutput(true);
    serverConn.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");
    OutputStreamWriter nw = new OutputStreamWriter(serverConn.getOutputStream());
    String server = ApplyChangesServlet.PARAMETER_COMMAND + "=" + buffer;
    server += "&" + ApplyChangesServlet.PARAMETER_CAPSULE + "=" + URLEncoder.encode(changeCapsule.toJSON(), "UTF-8");
    nw.write(server);
    nw.flush();
    BufferedReader xml = new BufferedReader(new InputStreamReader(serverConn.getInputStream()));
    StringBuffer response = new StringBuffer();
    String str;
    while (null != ((str = xml.readLine()))) {
        response.append(str);
    }
    nw.close();
    xml.close();
    return response.toString();
}

