private void copy(File inputFile, File outputFile) throws Exception {
    FileReader in = new FileReader(inputFile);
    FileWriter out = new FileWriter(outputFile);
    int c;
    while ((c = in.read()) != -1) out.write(c);
    in.close();
    out.close();
}

public List<String> extractUrlList(String string) throws IOException, XPathExpressionException {
    LinkedList<String> dl = new LinkedList<String>();
    HttpURLConnection conn = (HttpURLConnection) new URL(string).openConnection();
    Tidy ttickle = new Tidy();
    ttickle.setErrout(new NullPrintWriter());
    Document oc = ttickle.parseDOM(conn.getInputStream(), null);
    int len = conn.getContentLength();
    if (len <= 0) len = 32000;
    ByteArrayOutputStream bbOUT = new ByteArrayOutputStream(len);
    PrintStream os = new PrintStream(bbOUT);
    ttickle.pprint(oc, os);
    os.flush();
    String media = bbOUT.toString();
    Pattern lp = Pattern.compile("(http://[\\w\\\\\\./=&?;-]+)");
    Matcher line = lp.matcher(media);
    while (line.find()) {
        dl.add(line.group());
    }
    return dl;
}

private void forBundle(BundleManipulator manip) {
    ByteArrayOutputStream bout = null;
    try {
        bout = new ByteArrayOutputStream();
        ZipOutputStream zout = new ZipOutputStream(bout);
        Bundle bundle = getBundle();
        Enumeration<URL> files = bundle.findEntries("/", "*.vm", false);
        if (files != null) {
            while (files.hasMoreElements()) {
                URL url = files.nextElement();
                String name = url.getFile();
                if (name.startsWith("/")) {
                    name = name.substring(1);
                }
                if (manip.includeEntry(name)) {
                    zout.putNextEntry(new ZipEntry(name));
                    IOUtils.copy(url.openStream(), zout);
                }
            }
        }
        manip.finish(bundle, zout);
        Manifest mf = new Manifest(bundle.getEntry("META-INF/MANIFEST.MF").openStream());
        zout.putNextEntry(new ZipEntry("META-INF/MANIFEST.MF"));
        mf.write(zout);
        zout.close();
        File tmpFile = File.createTempFile(TEMPLATES_SYMBOLIC_NAME, ".jar");
        FileUtils.writeByteArrayToFile(tmpFile, bout.toByteArray());
        if (pluginAccessor.getPlugin(TEMPLATES_SYMBOLIC_NAME) != null) {
            pluginController.uninstall(pluginAccessor.getPlugin(TEMPLATES_SYMBOLIC_NAME));
        } else if (pluginAccessor.getPlugin(TEMPLATES_PLUGIN_KEY) != null) {
            pluginController.uninstall(pluginAccessor.getPlugin(TEMPLATES_PLUGIN_KEY));
        }
        pluginController.installPlugin(new JarPluginArtifact(tmpFile));
        ServiceReference ref = bundleContext.getServiceReference(PackageAdmin.class.getName());
        ((PackageAdmin) bundleContext.getService(ref)).refreshPackages(null);
        tmpFile.delete();
    } catch (IOException e) {
        e.printStackTrace();
    } finally {
        IOUtils.closeQuietly(bout);
    }
}

    public void convert(File url, File dir) throws IOException {
        InputStream in = new BufferedInputStream(new FileInputStream(url));
        DcmParser p = pfact.newDcmParser(in);
        Dataset ds = fact.newDataset();
        p.setDcmHandler(ds.getDcmHandler());
        try {
            FileFormat format = p.detectFileFormat();
            if (format != FileFormat.ACRNEMA_STREAM) {
                System.out.println("
" + src + ": not an ACRNEMA stream!");
                return;
            }
            p.parseDcmFile(format, Tags.PixelData);
            if (ds.contains(Tags.StudyInstanceUID) || ds.contains(Tags.SeriesInstanceUID) || ds.contains(Tags.SOPInstanceUID) || ds.contains(Tags.SOPClassUID)) {
                System.out.println("
" + src + ": contains UIDs!" + " => probable already DICOM - do not convert");
                return;
            }
            boolean hasPixelData = p.getReadTag() == Tags.PixelData;
            boolean inflate = hasPixelData && ds.getInt(Tags.BitsAllocated, 0) == 12;
            int pxlen = p.getReadLength();
            if (hasPixelData) {
                if (inflate) {
                    ds.putUS(Tags.BitsAllocated, 16);
                    pxlen = pxlen * 4 / 3;
                }
                if (pxlen != (ds.getInt(Tags.BitsAllocated, 0) >>> 3) * ds.getInt(Tags.Rows, 0) * ds.getInt(Tags.Columns, 0) * ds.getInt(Tags.NumberOfFrames, 1) * ds.getInt(Tags.NumberOfSamples, 1)) {
                    System.out.println("
" + src + ": mismatch pixel data length!" + " => do not convert");
                    return;
                }
            }
            ds.putUI(Tags.StudyInstanceUID, uid(studyUID));
            ds.putUI(Tags.SeriesInstanceUID, uid(seriesUID));
            ds.putUI(Tags.SOPInstanceUID, uid(instUID));
            ds.putUI(Tags.SOPClassUID, classUID);
            if (!ds.contains(Tags.NumberOfSamples)) {
                ds.putUS(Tags.NumberOfSamples, 1);
            }
            if (!ds.contains(Tags.PhotometricInterpretation)) {
                ds.putCS(Tags.PhotometricInterpretation, "MONOCHROME2");
            }
            if (fmi) {
                ds.setFileMetaInfo(fact.newFileMetaInfo(ds, UIDs.ImplicitVRLittleEndian));
            }
            OutputStream out = new BufferedOutputStream(new FileOutputStream(dir));
            try {
            } finally {
                ds.writeFile(out, encodeParam());
                if (hasPixelData) {
                    if (!skipGroupLen) {
                        out.write(PXDATA_GROUPLEN);
                        int grlen = pxlen + 8;
                        out.write((byte) grlen);
                        out.write((byte) (grlen >> 8));
                        out.write((byte) (grlen >> 16));
                        out.write((byte) (grlen >> 24));
                    }
                    out.write(PXDATA_TAG);
                    out.write((byte) pxlen);
                    out.write((byte) (pxlen >> 8));
                    out.write((byte) (pxlen >> 16));
                    out.write((byte) (pxlen >> 24));
                }
                if (inflate) {
                    int b2, b3;
                    for (; pxlen > 0; pxlen -= 3) {
                        out.write(in.read());
                        b2 = in.read();
                        b3 = in.read();
                        out.write(b2 & 0x0f);
                        out.write(b2 >> 4 | ((b3 & 0x0f) << 4));
                        out.write(b3 >> 4);
                    }
                } else {
                    for (; pxlen > 0; --pxlen) {
                        out.write(in.read());
                    }
                }
                out.close();
            }
            System.out.print('.');
        } finally {
            in.close();
        }
    }

