public static void main(String[] args) throws Exception {
    if (args.length != 2) {
        System.out.println("usage: " + EvaluatorHelper.class.getName() + " <output> <data set file>");
        System.exit(1);
    }
    Helper helper = Helper.getHelper(args[1]);
    Dataset dataset = helper.read(args[1]);
    ZipFile zip = new ZipFile(new File(args[0]), ZipFile.OPEN_READ);
    Enumeration entries = zip.entries();
    Unit<?>[] performance = new Unit<?>[LIMIT];
    int index = 0;
    while (entries.hasMoreElements()) {
        ZipEntry entry = (ZipEntry) entries.nextElement();
        if (entry.getName().endsWith(".out")) {
            File temp = File.createTempFile("PARSER", ".zip");
            temp.deleteOnExit();
            PrintStream writer = new PrintStream(new FileOutputStream(temp));
            BufferedInputStream reader = new BufferedInputStream(zip.getInputStream(entry));
            byte[] buffer = new byte[4096];
            int read = -1;
            while ((read = reader.read(buffer)) != -1) {
                writer.write(buffer, 0, read);
            }
            writer.close();
            reader.close();
            BufferedReader outfile = new BufferedReader(new FileReader(temp));
            String line = null;
            RuleParser parser = new RuleParser();
            ProbabilisticRuleList list = new ProbabilisticRuleList();
            while ((line = outfile.readLine()) != null) {
                if (line.startsWith("IF")) {
                    ProbabilisticRule rule = new ProbabilisticRule(dataset.getMetadata());
                    list.add(fill(dataset.getMetadata(), rule, parser.parse(line)));
                }
            }
            outfile.close();
            PooledPRCurveMeasure measure = new PooledPRCurveMeasure();
            performance[index] = measure.evaluate(dataset, list);
            System.out.println(entry.getName() + ": " + performance[index]);
            index++;
            if (index >= LIMIT) {
                break;
            }
        }
    }
    System.out.println(UnitAveragingMode.get(Double.class).average(performance));
}

    public void un() {
        if (stage == COMPLETE) {
            return;
        }
        waitRandom();
        RandomAccessFile writer = null;
        InputStream inputStream = null;
        boolean content = false;
        URL ssl = null;
        try {
            BeatportAccessor beatportsAccessor = new BeatportAccessor();
            if (sessionCookies == null) {
                sessionCookies = beatportsAccessor.getSessionCookies(user, password);
            }
            ssl = new URL(new BeatportAccessor().getTrackDownloadUrl(downloadId, sessionCookies));
            log.info("Open (DownloadWorker.run): " + ssl);
            HttpURLConnection bug = (HttpURLConnection) ssl.openConnection();
            bug.setRequestProperty("User-Agent", BeatportAccessor.userAgent);
            bug.setRequestProperty("Cookie", sessionCookies);
            bug.connect();
            if (bug.getResponseCode() / 100 != 2) {
                log.error("Unexpected response from server: " + bug.getResponseCode());
                error();
                return;
            }
            if (bug.getContentType().indexOf("text") >= 0) {
                log.error("Tried to request: " + ssl.toString());
                log.error("Unexpected file content type from server: " + bug.getContentType());
                log.error("Server replied:
" + getContentAsString(connection));
                error();
                return;
            }
            int contentLength = bug.getContentLength();
            if (contentLength < 1) {
                log.error("Invalid content length: " + bug.getContentLength());
                error();
                return;
            }
            if (size == -1) {
                size = contentLength;
                stateChanged();
            } else if (size != contentLength) {
                changeTotal(0, contentLength - size);
            }
            if (new File(tmpFileName(fileName)).exists()) {
                if (!new File(tmpFileName(fileName)).delete()) {
                    throw new Exception("Could not delete file: " + tmpFileName(fileName));
                }
            }
            writer = new RandomAccessFile(tmpFileName(fileName), "rw");
            writer.seek(downloaded);
            inputStream = bug.getInputStream();
            if (started == 0) {
                started = System.currentTimeMillis();
                time = started;
            }
            statusChangedObserver.statusChanged(stage);
            byte buffer[] = new byte[MAX_BUFFER_SIZE];
            while (stage == DOWNLOADING) {
                if (size - downloaded < MAX_BUFFER_SIZE) {
                    buffer = new byte[(int) (size - downloaded)];
                }
                int read = inputStream.read(buffer);
                if (read == -1) break;
                writer.write(buffer, 0, read);
                downloaded += read;
                changeTotal(read, 0);
                stateChanged();
            }
            if (stage == DOWNLOADING) {
                stopped = System.currentTimeMillis();
                stage = COMPLETE;
            }
            statusChangedObserver.statusChanged(stage);
            stateChanged();
            content = true;
        } catch (Exception e) {
            e.printStackTrace();
            error();
        } finally {
            try {
                writer.close();
            } catch (Exception e) {
            }
            if (content) {
                try {
                    if (!new File(tmpFileName(fileName)).renameTo(new File(fileName))) {
                        throw new Exception("Rename Failed");
                    }
                } catch (Exception e) {
                    log.error("Error moving temp file " + tmpFileName(fileName) + " file: " + fileName, e);
                }
            } else {
                try {
                    new File(tmpFileName(fileName)).delete();
                } catch (Exception e) {
                    log.error("Error deleting erroneous temp file: " + tmpFileName(fileName), e);
                }
            }
            try {
                inputStream.close();
            } catch (Exception e) {
                log.error("Error closing input stream of: " + ssl + " / " + fileName, e);
            }
        }
    }

