public static boolean encodeFileToFile(String infile, String outfile) {
    boolean success = false;
    java.io.InputStream in = null;
    java.io.OutputStream out = null;
    try {
        in = new Base64.InputStream(new java.io.BufferedInputStream(new java.io.FileInputStream(infile)), Base64.ENCODE);
        out = new java.io.BufferedOutputStream(new java.io.FileOutputStream(outfile));
        byte[] buffer = new byte[65536];
        int read = -1;
        while ((read = in.read(buffer)) >= 0) {
            out.write(buffer, 0, read);
        }
        success = true;
    } catch (java.io.IOException exc) {
        exc.printStackTrace();
    } finally {
        try {
            in.close();
        } catch (Exception exc) {
        }
        try {
            out.close();
        } catch (Exception exc) {
        }
    }
    return success;
}

private String getFullFilePath(URL source, String shortNumber) throws Exception {
    JarInputStream ajis = new JarInputStream(source.openStream());
    ZipEntry gzconnection = null;
    while ((gzconnection = ajis.getNextEntry()) != null) {
        String file = gzconnection.getName();
        int nextPart = file.lastIndexOf(".class");
        if (nextPart < 0) {
            continue;
        }
        file = file.replace('/', '.');
        int line = -1;
        if (shortNumber != null) {
            line = file.indexOf(shortNumber);
            if (line >= 0 && file.length() == line + shortNumber.length() + 6) {
                ajis.close();
                return (file.substring(0, nextPart));
            }
        }
    }
    ajis.close();
    return (null);
}

