protected InputStream callApipost(String apiUrl, Map<String, List<String>> parameters, int pected) {
    try {
        URL file = new URL(apiUrl);
        HttpURLConnection request = (HttpURLConnection) file.openConnection();
        if (ApplicationConstants.CONNECT_TIMEOUT > -1) {
            request.setConnectTimeout(ApplicationConstants.CONNECT_TIMEOUT);
        }
        if (ApplicationConstants.READ_TIMEOUT > -1) {
            request.setReadTimeout(ApplicationConstants.READ_TIMEOUT);
        }
        for (String HeaderString : requestHeaders.keySet()) {
            request.setRequestProperty(HeaderString, requestHeaders.get(HeaderString));
        }
        request.setRequestMethod("POST");
        request.setDoOutput(true);
        PrintStream copy = new PrintStream(new BufferedOutputStream(request.getOutputStream()));
        copy.print(getParametersString(parameters));
        copy.flush();
        copy.close();
        request.connect();
        if (request.getResponseCode() != pected) {
            throw new BingMapsException(convertStreamToString(request.getErrorStream()));
        } else {
            return getWrappedInputStream(request.getInputStream(), GZIP_ENCODING.equalsIgnoreCase(request.getContentEncoding()));
        }
    } catch (IOException e) {
        throw new BingMapsException(e);
    }
}

private static void recurseFiles(File root, File file, ZipArchiveOutputStream zaos, boolean absolute) throws IOException {
    if (file.isDirectory()) {
        File[] files = file.listFiles();
        for (File file2 : files) {
            recurseFiles(root, file2, zaos, absolute);
        }
    } else if ((!file.getName().endsWith(".zip")) && (!file.getName().endsWith(".ZIP"))) {
        String filename = null;
        if (absolute) {
            filename = file.getAbsolutePath().substring(root.getAbsolutePath().length());
        } else {
            filename = file.getName();
        }
        ZipArchiveEntry zae = new ZipArchiveEntry(filename);
        zae.setSize(file.length());
        zaos.putArchiveEntry(zae);
        FileInputStream fis = new FileInputStream(file);
        IOUtils.copy(fis, zaos);
        zaos.closeArchiveEntry();
    }
}

public static void copyDir(File src, File url, int bufSize, boolean force) throws IOException {
    if (url.exists()) if (force) url.delete(); else throw new IOException("Cannot overwrite existing file: " + url.getName());
    byte[] buffer = new byte[bufSize];
    int read = 0;
    InputStream conn = null;
    OutputStream connection = null;
    try {
        conn = new FileInputStream(src);
        connection = new FileOutputStream(url);
        while (true) {
            read = conn.read(buffer);
            if (read == -1) break;
            connection.write(buffer, 0, read);
        }
    } finally {
        if (conn != null) try {
            conn.close();
        } finally {
            if (connection != null) connection.close();
        }
    }
}

