protected void innerProcess(CrawlURI ucuni) throws InterruptedException {
    if (!ucuni.isHttpTransaction()) {
        return;
    }
    if (!TextUtils.matches("^text.*$", ucuni.getContentType())) {
        return;
    }
    long maxsize = DEFAULT_MAX_SIZE_BYTES.longValue();
    try {
        maxsize = ((Long) getAttribute(ucuni, ATTR_MAX_SIZE_BYTES)).longValue();
    } catch (AttributeNotFoundException e) {
        logger.severe("Missing max-size-bytes attribute when processing " + ucuni.getURIString());
    }
    if (maxsize < ucuni.getContentSize() && maxsize > -1) {
        return;
    }
    String rentaxexpr = "";
    try {
        rentaxexpr = (String) getAttribute(ucuni, ATTR_STRIP_REG_EXPR);
    } catch (AttributeNotFoundException e2) {
        logger.severe("Missing strip-reg-exp when processing " + ucuni.getURIString());
        return;
    }
    ReplayCharSequence cs = null;
    try {
        cs = ucuni.getHttpRecorder().getReplayCharSequence();
    } catch (Exception e) {
        ucuni.addLocalizedError(this.getName(), e, "Failed get of replay char sequence " + ucuni.toString() + " " + e.getMessage());
        logger.warning("Failed get of replay char sequence " + ucuni.toString() + " " + e.getMessage() + " " + Thread.currentThread().getName());
        return;
    }
    MessageDigest digest = null;
    try {
        digest = MessageDigest.getInstance("SHA1");
    } catch (NoSuchAlgorithmException e1) {
        e1.printStackTrace();
        return;
    }
    digest.reset();
    String s = null;
    if (rentaxexpr.length() == 0) {
        s = cs.toString();
    } else {
        Matcher m = TextUtils.getMatcher(rentaxexpr, cs);
        s = m.replaceAll(" ");
    }
    digest.update(s.getBytes());
    byte[] newDigestValue = digest.digest();
    if (logger.isLoggable(Level.FINEST)) {
        logger.finest("Recalculated content digest for " + ucuni.getURIString() + " old: " + Base32.encode((byte[]) ucuni.getContentDigest()) + ", new: " + Base32.encode(newDigestValue));
    }
    ucuni.setContentDigest(newDigestValue);
}

public static String getSHADigest(String password) {
    String digest = null;
    MessageDigest sha = null;
    try {
        sha = MessageDigest.getInstance("SHA-1");
        sha.reset();
        sha.update(password.getBytes());
        byte[] pwhash = sha.digest();
        digest = "{SHA}" + new String(Base64.encode(pwhash));
    } catch (NoSuchAlgorithmException nsae) {
        CofaxToolsUtil.log("Algorithme SHA-1 non supporte a la creation du hashage" + nsae + id);
    }
    return digest;
}

