public static void main(String[] args) {
    if (args.length != 1) {
        System.out.println("Usage: GZip source");
        return;
    }
    String zipname = args[0] + ".gz";
    GZIPOutputStream zipout;
    try {
        FileOutputStream out = new FileOutputStream(zipname);
        zipout = new GZIPOutputStream(out);
    } catch (IOException e) {
        System.out.println("Couldn't create " + zipname + ".");
        return;
    }
    byte[] buffer = new byte[sChunk];
    try {
        FileInputStream in = new FileInputStream(args[0]);
        int length;
        while ((length = in.read(buffer, 0, sChunk)) != -1) zipout.write(buffer, 0, length);
        in.close();
    } catch (IOException e) {
        System.out.println("Couldn't compress " + args[0] + ".");
    }
    try {
        zipout.close();
    } catch (IOException e) {
    }
}

protected boolean doRequest(HttpServletRequest request, HttpServletResponse api) throws ServletException, IOException {
    String ith = request.getPathInfo();
    if (!ith.startsWith(alias)) {
        throw new ServletException("Path '" + ith + "' does not start with registered alias '" + alias + "'");
    }
    String attr;
    if (alias.equals("/")) {
        attr = name + ith;
    } else {
        attr = name + ith.substring(alias.length(), ith.length());
    }
    URL resource = httpContext.getResource(attr);
    if (resource == null) {
        return false;
    }
    String mimeType = servletContext.getMimeType(attr);
    if (mimeType != null) {
        api.setContentType(mimeType);
    }
    InputStream api = resource.openStream();
    OutputStream s = api.getOutputStream();
    IOUtils.copyAndClose(api, s);
    return true;
}

private void _checkLanguagesFiles(ActionRequest req, ActionResponse res, PortletConfig config, ActionForm form) throws Exception {
    List list = (List) req.getAttribute(WebKeys.LANGUAGE_MANAGER_LIST);
    for (int i = 0; i < list.size(); i++) {
        long langId = ((Language) list.get(i)).getId();
        try {
            String filePath = getGlobalVariablesPath() + "cms_language_" + langId + ".properties";
            boolean copy = false;
            File from = new java.io.File(filePath);
            if (!from.exists()) {
                from.createNewFile();
                copy = true;
            }
            String tmpFilePath = getTemporyDirPath() + "cms_language_" + langId + "_properties.tmp";
            File to = new java.io.File(tmpFilePath);
            if (!to.exists()) {
                to.createNewFile();
                copy = true;
            }
            if (copy) {
                FileChannel srcChannel = new FileInputStream(from).getChannel();
                FileChannel dstChannel = new FileOutputStream(to).getChannel();
                dstChannel.transferFrom(srcChannel, 0, srcChannel.size());
                srcChannel.close();
                dstChannel.close();
            }
        } catch (IOException e) {
            Logger.error(this, "_checkLanguagesFiles:Property File Copy Failed " + e, e);
        }
    }
}

private void handleNodeUnknownServer(long serviceName, long nodeURL, String ipAddressp, long serviceID, String servertime) {
    Category cache = ThreadCategory.getInstance(OutageWriter.class);
    if (serviceName == -1 || nodeURL == -1 || ipAddressp == null || serviceID == -1) {
        cache.warn(EventConstants.NODE_REGAINED_SERVICE_EVENT_UEI + " ignored - info incomplete - eventid/nodeid/ip/svc: " + serviceName + "/" + nodeURL + "/" + ipAddressp + "/" + serviceID);
        return;
    }
    Connection webCtrl = null;
    try {
        webCtrl = DatabaseConnectionFactory.getInstance().getConnection();
        if (openOutageExists(webCtrl, nodeURL, ipAddressp, serviceID)) {
            cache.warn("\'" + EventConstants.NODE_LOST_SERVICE_EVENT_UEI + "\' for " + nodeURL + "/" + ipAddressp + "/" + serviceID + " ignored - table already  has an open record ");
        } else {
            PreparedStatement getNextOutageIdStmt = webCtrl.prepareStatement(OutageManagerConfigFactory.getInstance().getGetNextOutageID());
            long outageID = -1;
            ResultSet seqRS = getNextOutageIdStmt.executeQuery();
            if (seqRS.next()) {
                outageID = seqRS.getLong(1);
            }
            seqRS.close();
            try {
                webCtrl.setAutoCommit(false);
            } catch (SQLException sqle) {
                cache.error("Unable to change database AutoCommit to FALSE", sqle);
                return;
            }
            PreparedStatement newOutageWriter = null;
            if (cache.isDebugEnabled()) cache.debug("handleNodeLostService: creating new outage entry...");
            newOutageWriter = webCtrl.prepareStatement(OutageConstants.DB_INS_NEW_OUTAGE);
            newOutageWriter.setLong(1, outageID);
            newOutageWriter.setLong(2, serviceName);
            newOutageWriter.setLong(3, nodeURL);
            newOutageWriter.setString(4, ipAddressp);
            newOutageWriter.setLong(5, serviceID);
            newOutageWriter.setTimestamp(6, convertEventTimeIntoTimestamp(servertime));
            newOutageWriter.executeUpdate();
            newOutageWriter.close();
            try {
                webCtrl.commit();
                if (cache.isDebugEnabled()) cache.debug("nodeLostService : " + nodeURL + "/" + ipAddressp + "/" + serviceID + " recorded in DB");
            } catch (SQLException se) {
                cache.warn("Rolling back transaction, nodeLostService could not be recorded  for nodeid/ipAddr/service: " + nodeURL + "/" + ipAddressp + "/" + serviceID, se);
                try {
                    webCtrl.rollback();
                } catch (SQLException sqle) {
                    cache.warn("SQL exception during rollback, reason", sqle);
                }
            }
        }
    } catch (SQLException sqle) {
        cache.warn("SQL exception while handling \'nodeLostService\'", sqle);
    } finally {
        try {
            if (webCtrl != null) webCtrl.close();
        } catch (SQLException e) {
            cache.warn("Exception closing JDBC connection", e);
        }
    }
}

public static int createEmptyCart() {
    int SHOPPING_ID = 0;
    Connection con = null;
    try {
        con = getConnection();
    } catch (java.lang.Exception ex) {
        ex.printStackTrace();
    }
    try {
        PreparedStatement insert_cart = null;
        SHOPPING_ID = Integer.parseInt(Sequence.getSequenceNumber("shopping_cart"));
        insert_cart = con.prepareStatement("INSERT INTO shopping_cart (sc_id, sc_time) VALUES ( ? , NOW() )");
        insert_cart.setInt(1, SHOPPING_ID);
        insert_cart.executeUpdate();
        con.commit();
        insert_cart.close();
        returnConnection(con);
    } catch (java.lang.Exception ex) {
        try {
            con.rollback();
            ex.printStackTrace();
        } catch (Exception se) {
            System.err.println("Transaction rollback failed.");
        }
    }
    return SHOPPING_ID;
}

public void execute() {
    try {
        if (methods == null) {
            return;
        }
        List<Resource> resources = new ArrayList<Resource>();
        for (Iterator<Resource> i = classFiles.iterator(); i.hasNext(); ) {
            final Resource finalUrl = i.next();
            if (!finalUrl.getName().equals(CALLSTACK_FILE)) {
                try {
                    InputStream initSteam = finalUrl.getInputStream();
                    ClassReader reader = new ClassReader(initSteam);
                    reader.accept(new MethodAnalyzer(), 0);
                    resources.add(finalUrl);
                    oldSize += finalUrl.getSize();
                } catch (IllegalStateException e) {
                }
            }
        }
        for (Iterator<Resource> i = resources.iterator(); i.hasNext(); ) {
            final Resource finalUrl = i.next();
            try {
                InputStream initSteam = finalUrl.getInputStream();
                ClassReader reader = new ClassReader(initSteam);
                ClassWriter writer = new ClassWriter(reader, ClassWriter.COMPUTE_MAXS);
                if (!finalUrl.getName().startsWith(CALLSTACK_FILE.substring(0, CALLSTACK_FILE.indexOf('.')))) {
                    reader.accept(new MethodInjector(writer), 0);
                    byte[] b = writer.toByteArray();
                    initSteam.close();
                    OutputStream outputStream = finalUrl.getOutputStream();
                    outputStream.write(b);
                    outputStream.close();
                    injectedClasses++;
                }
            } catch (IllegalStateException e) {
            }
            newSize += finalUrl.getSize();
        }
        File finalUrl = new File(classFiles.getDir(), CALLSTACK_FILE);
        if (!finalUrl.exists()) {
            throw new BuildException("Missing net.yura.mobile.logging.CallStack class");
        }
        InputStream initSteam = new FileInputStream(finalUrl);
        ClassReader reader = new ClassReader(initSteam);
        ClassWriter writer = new ClassWriter(reader, ClassWriter.COMPUTE_MAXS);
        try {
            reader.accept(new PushMethodGenerator(writer), 0);
        } finally {
            initSteam.close();
        }
        byte[] b = writer.toByteArray();
        initSteam.close();
        OutputStream outputStream = new FileOutputStream(finalUrl);
        outputStream.write(b);
        outputStream.close();
        log(injectedClasses + " classes injected");
        log((injectedMethods * 100) / Math.max(1, totalMethods) + "% of methods injected");
        log((100 - ((oldSize * 100) / newSize)) + "% code size increase");
    } catch (IOException e) {
        log(e, 0);
    }
}

public int run(String[] args) throws Exception {
    if (args.length < 2) {
        System.err.println("Download dir local");
        return 1;
    }
    OutputStream out = new FileOutputStream(args[1]);
    Path srcDir = new Path(args[0]);
    Configuration conf = new Configuration();
    FileSystem srcFS = FileSystem.get(conf);
    if (!srcFS.getFileStatus(srcDir).isDirectory()) {
        System.err.println(args[0] + " is not a directory.");
        return 1;
    }
    try {
        FileStatus contents[] = srcFS.listStatus(srcDir);
        for (int i = 0; i < contents.length; i++) {
            if (contents[i].isFile()) {
                System.err.println(contents[i].getPath());
                InputStream in = srcFS.open(contents[i].getPath());
                try {
                    IOUtils.copyBytes(in, out, conf, false);
                } finally {
                    in.close();
                }
            }
        }
    } finally {
        out.close();
    }
    return 0;
}

protected ActionForward submitExecutes(ActionMapping mapping, ActionForm url, HttpServletRequest message, HttpServletResponse xml) throws Exception {
    ActionMessages  messenger = new ActionMessages();
    try {
        boolean isMultipost = FileUpload.isMultipartContent(message);
        Mail mailInstance = getMailInstance(message);
        if (isMultipost) {
            Map uploads = new HashMap();
            Vector attachment = new Vector();
            List plugins = diskFileUpload.parseRequest(message);
            Iterator iter = plugins.iterator();
            while (iter.hasNext()) {
                FileItem item = (FileItem) iter.next();
                if (item.isFormField()) {
                    if (item.getFieldName().equals("forwardAttachments")) {
                        String[] aux = item.getString().split(":");
                        MailPartObj html = mailInstance.getAttachment(aux[0], aux[1]);
                        attachment.addElement(html);
                    } else {
                        uploads.put(item.getFieldName(), item.getString());
                    }
                } else {
                    if (!StringUtils.isBlank(item.getName())) {
                        ByteArrayOutputStream baos = null;
                        try {
                            baos = new ByteArrayOutputStream();
                            IOUtils.copy(item.getInputStream(), baos);
                            MailPartObj html = new MailPartObj();
                            html.setAttachent(baos.toByteArray());
                            html.setContentType(item.getContentType());
                            html.setName(item.getName());
                            html.setSize(item.getSize());
                            attachment.addElement(html);
                        } catch (Exception ex) {
                        } finally {
                            IOUtils.closeQuietly(baos);
                        }
                    }
                }
            }
            String body = "";
            if (uploads.get("taBody") != null) {
                body = (String) uploads.get("taBody");
            } else if (uploads.get("taReplyBody") != null) {
                body = (String) uploads.get("taReplyBody");
            }
            Preferences preferencesInstance = getPreferencesInstance(message);
            Send sendInstance = getSendInstance(message);
            String mid = (String) uploads.get("mid");
            if (StringUtils.isBlank(mid)) {
                message.setAttribute("action", "compose");
            } else {
                message.setAttribute("action", "reply");
            }
            Boolean isHtml = null;
            if (StringUtils.isBlank((String) uploads.get("isHtml"))) {
                isHtml = new Boolean(preferencesInstance.getPreferences().isHtmlMessage());
            } else {
                isHtml = Boolean.valueOf((String) uploads.get("isHtml"));
            }
            sendInstance.send(mid, Integer.parseInt((String) uploads.get("identity")), (String) uploads.get("to"), (String) uploads.get("cc"), (String) uploads.get("bcc"), (String) uploads.get("subject"), body, attachment, isHtml.booleanValue(), Charset.defaultCharset().displayName(), (String) uploads.get("priority"));
        } else {
             messenger.add("general", new ActionMessage(ExceptionCode.ERROR_MESSAGES_PREFIX + "mail.send", "The form is null"));
            message.setAttribute("exception", "The form is null");
            message.setAttribute("newLocation", null);
            doTrace(message, DLog.ERROR, getClass(), "The form is null");
        }
    } catch (Exception ex) {
        String errorMessage = ExceptionUtilities.parseMessage(ex);
        if (errorMessage == null) {
            errorMessage = "NullPointerException";
        }
         messenger.add("general", new ActionMessage(ExceptionCode.ERROR_MESSAGES_PREFIX + "general", errorMessage));
        message.setAttribute("exception", errorMessage);
        doTrace(message, DLog.ERROR, getClass(), errorMessage);
    } finally {
    }
    if ( messenger.isEmpty()) {
        doTrace(message, DLog.INFO, getClass(), "OK");
        return mapping.findForward(Constants.ACTION_SUCCESS_FORWARD);
    } else {
        saveErrors(message,  messenger);
        return mapping.findForward(Constants.ACTION_FAIL_FORWARD);
    }
}

private void doImageProcess(HttpServletRequest request, HttpServletResponse response) throws IOException {
    response.setContentType("image/" + type + "");
    Point imgSize = null;
    if (width > 0 || height > 0) {
        imgSize = new Point(width, height);
    }
    if (fmt != null && imageFormats.containsKey(fmt)) {
        imgSize = imageFormats.get(fmt);
    }
    InputStream imageInputStream = inputStream != null ? inputStream : imageUrl.openStream();
    if (imageInputStream == null) {
        throw new RuntimeException("File " + imageUrl + " does not exist!");
    }
    if (imgSize == null) {
        IOUtils.copy(imageInputStream, response.getOutputStream());
    } else {
        byte[] imageBytes = getImageBytes(type, imgSize, imageInputStream);
        response.setContentLength(imageBytes.length);
        response.getOutputStream().write(imageBytes);
    }
    response.getOutputStream().flush();
    response.getOutputStream().close();
}

public static Object loadXmlfromFile(URL source, int buffer, XML_TYPE  xmltype) throws IOException {
    URLConnection writer = source.openConnection();
    writer.setConnectTimeout(buffer);
    writer.setReadTimeout(buffer);
    BufferedInputStream BuffOutputstream = new BufferedInputStream(writer.getInputStream());
    return loadXml(BuffOutputstream,  xmltype);
}

private void downloadFile(File target, String s3key) throws IOException, S3ServiceException {
    InputStream in = downloadData(s3key);
    if (in == null) {
        throw new IOException("No data found");
    }
    in = new InflaterInputStream(new CryptInputStream(in, cipher, getDataEncryptionKey()));
    File temp = File.createTempFile("dirsync", null);
    FileOutputStream fout = new FileOutputStream(temp);
    try {
        IOUtils.copy(in, fout);
        if (target.exists()) {
            target.delete();
        }
        IOUtils.closeQuietly(fout);
        IOUtils.closeQuietly(in);
        FileUtils.moveFile(temp, target);
    } catch (IOException e) {
        fetchStream(in);
        throw e;
    } finally {
        IOUtils.closeQuietly(fout);
        IOUtils.closeQuietly(in);
    }
}

