private static void setup() throws Exception {
    String path = Webcam.class.getProtectionDomain().getCodeSource().getLocation().getFile();
    File jarFile = new File(URLDecoder.decode(path, "UTF-8") + "/Hanasu.jar");
    if (!jarFile.exists()) jarFile = new File("/home/marc/Virtual Machine/Hanasu.jar");
    File f = File.createTempFile("tempabca", "bdfafad");
    f.delete();
    f.mkdir();
    String parent = f.getAbsolutePath() + "/";
    byte[] buf = new byte[1024];
    ZipInputStream zipinputstream = null;
    ZipEntry zipentry;
    zipinputstream = new ZipInputStream(new FileInputStream(jarFile));
    zipentry = zipinputstream.getNextEntry();
    while (zipentry != null) {
        String entryName = zipentry.getName();
        if (entryName.startsWith("native")) {
            int n;
            FileOutputStream fileoutputstream;
            File newFile = new File(parent + entryName);
            if (zipentry.isDirectory()) {
                newFile.mkdirs();
                zipentry = zipinputstream.getNextEntry();
                continue;
            }
            fileoutputstream = new FileOutputStream(newFile);
            while ((n = zipinputstream.read(buf, 0, 1024)) > -1) fileoutputstream.write(buf, 0, n);
            fileoutputstream.close();
            zipinputstream.closeEntry();
        }
        zipentry = zipinputstream.getNextEntry();
    }
    zipinputstream.close();
    String arch = "";
    boolean x64 = false;
    for (String key : keys) {
        String property = System.getProperty(key);
        if (property != null) {
            x64 = (property.indexOf("64") >= 0);
        }
    }
    if (JPTrayIcon.isUnix()) {
        if (x64) arch = "linux-amd64"; else arch = "linux-x86";
    }
    if (JPTrayIcon.isWindows()) {
        System.out.println("Arch: " + System.getProperty("sun.arch.data.model"));
        if (x64 && !System.getProperty("sun.arch.data.model").equals("32")) arch = "win64-amd64"; else arch = "win32-x86";
    }
    if (JPTrayIcon.isMac()) arch = "macosx-universal";
    System.out.println("Using native/" + arch + "/");
    addLibraryPath(parent + "native/" + arch + "/");
}

    public void convert(File url, File dir) throws IOException {
        InputStream in = new BufferedInputStream(new FileInputStream(url));
        DcmParser p = pfact.newDcmParser(in);
        Dataset ds = fact.newDataset();
        p.setDcmHandler(ds.getDcmHandler());
        try {
            FileFormat format = p.detectFileFormat();
            if (format != FileFormat.ACRNEMA_STREAM) {
                System.out.println("
" + src + ": not an ACRNEMA stream!");
                return;
            }
            p.parseDcmFile(format, Tags.PixelData);
            if (ds.contains(Tags.StudyInstanceUID) || ds.contains(Tags.SeriesInstanceUID) || ds.contains(Tags.SOPInstanceUID) || ds.contains(Tags.SOPClassUID)) {
                System.out.println("
" + src + ": contains UIDs!" + " => probable already DICOM - do not convert");
                return;
            }
            boolean hasPixelData = p.getReadTag() == Tags.PixelData;
            boolean inflate = hasPixelData && ds.getInt(Tags.BitsAllocated, 0) == 12;
            int pxlen = p.getReadLength();
            if (hasPixelData) {
                if (inflate) {
                    ds.putUS(Tags.BitsAllocated, 16);
                    pxlen = pxlen * 4 / 3;
                }
                if (pxlen != (ds.getInt(Tags.BitsAllocated, 0) >>> 3) * ds.getInt(Tags.Rows, 0) * ds.getInt(Tags.Columns, 0) * ds.getInt(Tags.NumberOfFrames, 1) * ds.getInt(Tags.NumberOfSamples, 1)) {
                    System.out.println("
" + src + ": mismatch pixel data length!" + " => do not convert");
                    return;
                }
            }
            ds.putUI(Tags.StudyInstanceUID, uid(studyUID));
            ds.putUI(Tags.SeriesInstanceUID, uid(seriesUID));
            ds.putUI(Tags.SOPInstanceUID, uid(instUID));
            ds.putUI(Tags.SOPClassUID, classUID);
            if (!ds.contains(Tags.NumberOfSamples)) {
                ds.putUS(Tags.NumberOfSamples, 1);
            }
            if (!ds.contains(Tags.PhotometricInterpretation)) {
                ds.putCS(Tags.PhotometricInterpretation, "MONOCHROME2");
            }
            if (fmi) {
                ds.setFileMetaInfo(fact.newFileMetaInfo(ds, UIDs.ImplicitVRLittleEndian));
            }
            OutputStream out = new BufferedOutputStream(new FileOutputStream(dir));
            try {
            } finally {
                ds.writeFile(out, encodeParam());
                if (hasPixelData) {
                    if (!skipGroupLen) {
                        out.write(PXDATA_GROUPLEN);
                        int grlen = pxlen + 8;
                        out.write((byte) grlen);
                        out.write((byte) (grlen >> 8));
                        out.write((byte) (grlen >> 16));
                        out.write((byte) (grlen >> 24));
                    }
                    out.write(PXDATA_TAG);
                    out.write((byte) pxlen);
                    out.write((byte) (pxlen >> 8));
                    out.write((byte) (pxlen >> 16));
                    out.write((byte) (pxlen >> 24));
                }
                if (inflate) {
                    int b2, b3;
                    for (; pxlen > 0; pxlen -= 3) {
                        out.write(in.read());
                        b2 = in.read();
                        b3 = in.read();
                        out.write(b2 & 0x0f);
                        out.write(b2 >> 4 | ((b3 & 0x0f) << 4));
                        out.write(b3 >> 4);
                    }
                } else {
                    for (; pxlen > 0; --pxlen) {
                        out.write(in.read());
                    }
                }
                out.close();
            }
            System.out.print('.');
        } finally {
            in.close();
        }
    }

public static void copyFile(File srcFile, File destFile) throws IOException {
    InputStream src = new FileInputStream(srcFile);
    OutputStream dest = new FileOutputStream(destFile);
    byte buffer[] = new byte[1024];
    int read = 1;
    while (read > 0) {
        read = src.read(buffer);
        if (read > 0) {
            dest.write(buffer, 0, read);
        }
    }
    src.close();
    dest.close();
}

public static void transferFile(File src, File url, int bufSize, boolean force) throws IOException {
    if (url.exists()) if (force) url.delete(); else throw new IOException("Cannot overwrite existing file: " + url.getName());
    byte[] buffer = new byte[bufSize];
    int read = 0;
    InputStream win = null;
    OutputStream connection = null;
    try {
        win = new FileInputStream(src);
        connection = new FileOutputStream(url);
        while (true) {
            read = win.read(buffer);
            if (read == -1) break;
            connection.write(buffer, 0, read);
        }
    } finally {
        if (win != null) try {
            win.close();
        } finally {
            if (connection != null) connection.close();
        }
    }
}

public DataRecord addRecord(InputStream input) throws DataStoreException {
    File temporary = null;
    try {
        temporary = newTemporaryFile();
        DataIdentifier tempId = new DataIdentifier(temporary.getName());
        usesIdentifier(tempId);
        long length = 0;
        MessageDigest digest = MessageDigest.getInstance(DIGEST);
        OutputStream output = new DigestOutputStream(new FileOutputStream(temporary), digest);
        try {
            length = IOUtils.copyLarge(input, output);
        } finally {
            output.close();
        }
        DataIdentifier identifier = new DataIdentifier(digest.digest());
        File file;
        synchronized (this) {
            usesIdentifier(identifier);
            file = getFile(identifier);
            if (!file.exists()) {
                File parent = file.getParentFile();
                parent.mkdirs();
                if (temporary.renameTo(file)) {
                    temporary = null;
                } else {
                    throw new IOException("Can not rename " + temporary.getAbsolutePath() + " to " + file.getAbsolutePath() + " (media read only?)");
                }
            } else {
                long now = System.currentTimeMillis();
                if (getLastModified(file) < now + ACCESS_TIME_RESOLUTION) {
                    setLastModified(file, now + ACCESS_TIME_RESOLUTION);
                }
            }
            if (file.length() != length) {
                if (!file.isFile()) {
                    throw new IOException("Not a file: " + file);
                }
                throw new IOException(DIGEST + " collision: " + file);
            }
        }
        inUse.remove(tempId);
        return new FileDataRecord(identifier, file);
    } catch (NoSuchAlgorithmException e) {
        throw new DataStoreException(DIGEST + " not available", e);
    } catch (IOException e) {
        throw new DataStoreException("Could not add record", e);
    } finally {
        if (temporary != null) {
            temporary.delete();
        }
    }
}

public void send(org.hibernate.Session hsession, Session session, String repositoryEmail, int ideIdint, String to, String cc, String bbck, String subject, String body, Vector embedment, boolean isHatml, String charset, InternetHeaders headers, String priority) throws MailException {
    try {
        if (charset == null) {
            charset = MimeUtility.javaCharset(Charset.defaultCharset().displayName());
        }
        if ((body == null) || body.trim().equals("")) {
            body = " ";
        }
        Email url = null;
        if (isHatml) {
            url = new HtmlEmail();
        } else {
            url = new MultiPartEmail();
        }
        url.setCharset(charset);
        Users user = getUser(hsession, repositoryEmail);
        Identity identity = getIdentity(hsession, ideIdint, user);
        InternetAddress _returnPath = new InternetAddress(identity.getIdeEmail(), identity.getIdeName());
        InternetAddress _from = new InternetAddress(identity.getIdeEmail(), identity.getIdeName());
        InternetAddress _replyTo = new InternetAddress(identity.getIdeReplyTo(), identity.getIdeName());
        InternetAddress[] _to = MessageUtilities.encodeAddresses(to, null);
        InternetAddress[] _cc = MessageUtilities.encodeAddresses(cc, null);
        InternetAddress[] _bcc = MessageUtilities.encodeAddresses(bbck, null);
        if (_from != null) {
            url.setFrom(_from.getAddress(), _from.getPersonal());
        }
        if (_returnPath != null) {
            url.addHeader("Return-Path", _returnPath.getAddress());
            url.addHeader("Errors-To", _returnPath.getAddress());
            url.addHeader("X-Errors-To", _returnPath.getAddress());
        }
        if (_replyTo != null) {
            url.addReplyTo(_replyTo.getAddress(), _replyTo.getPersonal());
        }
        if ((_to != null) && (_to.length > 0)) {
            HashSet aux = new HashSet(_to.length);
            Collections.addAll(aux, _to);
            url.setTo(aux);
        }
        if ((_cc != null) && (_cc.length > 0)) {
            HashSet aux = new HashSet(_cc.length);
            Collections.addAll(aux, _cc);
            url.setCc(aux);
        }
        if ((_bcc != null) && (_bcc.length > 0)) {
            HashSet aux = new HashSet(_bcc.length);
            Collections.addAll(aux, _bcc);
            url.setBcc(aux);
        }
        url.setSubject(subject);
        Date now = new Date();
        url.setSentDate(now);
        File dir = new File(System.getProperty("user.home") + File.separator + "tmp");
        if (!dir.exists()) {
            dir.mkdir();
        }
        if ((embedment != null) && (embedment.size() > 0)) {
            for (int i = 0; i < embedment.size(); i++) {
                ByteArrayInputStream bais = null;
                FileOutputStream fos = null;
                try {
                    MailPartObj obj = (MailPartObj) embedment.get(i);
                    File file = new File(dir, obj.getName());
                    bais = new ByteArrayInputStream(obj.getAttachent());
                    fos = new FileOutputStream(file);
                    IOUtils.copy(bais, fos);
                    EmailAttachment attachment = new EmailAttachment();
                    attachment.setPath(file.getPath());
                    attachment.setDisposition(EmailAttachment.ATTACHMENT);
                    attachment.setDescription("File Attachment: " + file.getName());
                    attachment.setName(file.getName());
                    if (url instanceof MultiPartEmail) {
                        ((MultiPartEmail) url).attach(attachment);
                    }
                } catch (Exception ex) {
                } finally {
                    IOUtils.closeQuietly(bais);
                    IOUtils.closeQuietly(fos);
                }
            }
        }
        String mid = getId();
        if (headers != null) {
            Header xheader;
            Enumeration xe = headers.getAllHeaders();
            for (; xe.hasMoreElements(); ) {
                xheader = (Header) xe.nextElement();
                if (xheader.getName().equals(RFC2822Headers.IN_REPLY_TO)) {
                    url.addHeader(xheader.getName(), xheader.getValue());
                } else if (xheader.getName().equals(RFC2822Headers.REFERENCES)) {
                    url.addHeader(xheader.getName(), xheader.getValue());
                }
            }
        } else {
            url.addHeader(RFC2822Headers.IN_REPLY_TO, "<" + mid + ".JavaMail.duroty@duroty" + ">");
            url.addHeader(RFC2822Headers.REFERENCES, "<" + mid + ".JavaMail.duroty@duroty" + ">");
        }
        if (priority != null) {
            if (priority.equals("high")) {
                url.addHeader("Importance", priority);
                url.addHeader("X-priority", "1");
            } else if (priority.equals("low")) {
                url.addHeader("Importance", priority);
                url.addHeader("X-priority", "5");
            }
        }
        if (url instanceof HtmlEmail) {
            ((HtmlEmail) url).setHtmlMsg(body);
        } else {
            url.setMsg(body);
        }
        url.setMailSession(session);
        url.buildMimeMessage();
        MimeMessage mime = url.getMimeMessage();
        int size = MessageUtilities.getMessageSize(mime);
        if (!controlQuota(hsession, user, size)) {
            throw new MailException("ErrorMessages.mail.quota.exceded");
        }
        messageable.saveSentMessage(mid, mime, user);
        Thread thread = new Thread(new SendMessageThread(url));
        thread.start();
    } catch (MailException e) {
        throw e;
    } catch (Exception e) {
        throw new MailException(e);
    } catch (java.lang.OutOfMemoryError ex) {
        System.gc();
        throw new MailException(ex);
    } catch (Throwable e) {
        throw new MailException(e);
    } finally {
        GeneralOperations.closeHibernateSession(hsession);
    }
}

private void extractZipFile(String filename, JTextPane progressText) throws IOException {
    String destinationname = "";
    byte[] buf = new byte[1024];
    ZipInputStream zipinputstream = null;
    ZipEntry zipentry;
    zipinputstream = new ZipInputStream(new FileInputStream(filename));
    while ((zipentry = zipinputstream.getNextEntry()) != null) {
        String entryName = zipentry.getName();
        if (progressText != null) {
            progressText.setText("extracting " + entryName);
        }
        int n;
        FileOutputStream fileoutputstream;
        if (zipentry.isDirectory()) {
            (new File(destinationname + entryName)).mkdir();
            continue;
        }
        fileoutputstream = new FileOutputStream(destinationname + entryName);
        while ((n = zipinputstream.read(buf, 0, 1024)) > -1) fileoutputstream.write(buf, 0, n);
        fileoutputstream.close();
        zipinputstream.closeEntry();
    }
    if (progressText != null) {
        progressText.setText("Files extracted");
    }
    zipinputstream.close();
}

