static void copy(String src, String dest) throws IOException {
    File ifp = new File(src);
    File ofp = new File(dest);
    if (ifp.exists() == false) {
        throw new IOException("file '" + src + "' does not exist");
    }
    FileInputStream fis = new FileInputStream(ifp);
    FileOutputStream fos = new FileOutputStream(ofp);
    byte[] b = new byte[1024];
    while (fis.read(b) > 0) fos.write(b);
    fis.close();
    fos.close();
}

protected ActionForward submitExecute(ActionMapping matching, ActionForm url, HttpServletRequest message, HttpServletResponse xml) throws Exception {
    ActionMessages  messenger = new ActionMessages();
    try {
        boolean isMultipost = FileUpload.isMultipartContent(message);
        Mail mailinstance = getMailInstance(message);
        if (isMultipost) {
            Map uploads = new HashMap();
            Vector attachment = new Vector();
            List plugins = diskFileUpload.parseRequest(message);
            Iterator iter = plugins.iterator();
            while (iter.hasNext()) {
                FileItem item = (FileItem) iter.next();
                if (item.isFormField()) {
                    if (item.getFieldName().equals("forwardAttachments")) {
                        String[] aux = item.getString().split(":");
                        MailPartObj html = mailinstance.getAttachment(aux[0], aux[1]);
                        attachment.addElement(html);
                    } else {
                        uploads.put(item.getFieldName(), item.getString());
                    }
                } else {
                    if (!StringUtils.isBlank(item.getName())) {
                        ByteArrayOutputStream baos = null;
                        try {
                            baos = new ByteArrayOutputStream();
                            IOUtils.copy(item.getInputStream(), baos);
                            MailPartObj html = new MailPartObj();
                            html.setAttachent(baos.toByteArray());
                            html.setContentType(item.getContentType());
                            html.setName(item.getName());
                            html.setSize(item.getSize());
                            attachment.addElement(html);
                        } catch (Exception ex) {
                        } finally {
                            IOUtils.closeQuietly(baos);
                        }
                    }
                }
            }
            String body = "";
            if (uploads.get("taBody") != null) {
                body = (String) uploads.get("taBody");
            } else if (uploads.get("taReplyBody") != null) {
                body = (String) uploads.get("taReplyBody");
            }
            Preferences preferencesInstance = getPreferencesInstance(message);
            Send sendInstance = getSendInstance(message);
            String mid = (String) uploads.get("mid");
            if (StringUtils.isBlank(mid)) {
                message.setAttribute("action", "compose");
            } else {
                message.setAttribute("action", "reply");
            }
            Boolean isHtml = null;
            if (StringUtils.isBlank((String) uploads.get("isHtml"))) {
                isHtml = new Boolean(preferencesInstance.getPreferences().isHtmlMessage());
            } else {
                isHtml = Boolean.valueOf((String) uploads.get("isHtml"));
            }
            sendInstance.send(mid, Integer.parseInt((String) uploads.get("identity")), (String) uploads.get("to"), (String) uploads.get("cc"), (String) uploads.get("bcc"), (String) uploads.get("subject"), body, attachment, isHtml.booleanValue(), Charset.defaultCharset().displayName(), (String) uploads.get("priority"));
        } else {
             messenger.add("general", new ActionMessage(ExceptionCode.ERROR_MESSAGES_PREFIX + "mail.send", "The form is null"));
            message.setAttribute("exception", "The form is null");
            message.setAttribute("newLocation", null);
            doTrace(message, DLog.ERROR, getClass(), "The form is null");
        }
    } catch (Exception ex) {
        String errorMessage = ExceptionUtilities.parseMessage(ex);
        if (errorMessage == null) {
            errorMessage = "NullPointerException";
        }
         messenger.add("general", new ActionMessage(ExceptionCode.ERROR_MESSAGES_PREFIX + "general", errorMessage));
        message.setAttribute("exception", errorMessage);
        doTrace(message, DLog.ERROR, getClass(), errorMessage);
    } finally {
    }
    if ( messenger.isEmpty()) {
        doTrace(message, DLog.INFO, getClass(), "OK");
        return matching.findForward(Constants.ACTION_SUCCESS_FORWARD);
    } else {
        saveErrors(message,  messenger);
        return matching.findForward(Constants.ACTION_FAIL_FORWARD);
    }
}

@Override
public void doPost(HttpServletRequest request, HttpServletResponse response) throws IOException, ServletException {
    InputStream is = null;
    InputStream page = null;
    OutputStream os = null;
    String rootUrl = null;
    try {
        boolean isMultipart = ServletFileUpload.isMultipartContent(request);
        if (!isMultipart) {
            request.setAttribute("error", "Form isn't a multipart form");
            RequestDispatcher rd = request.getRequestDispatcher("/WEB-INF/error.jsp");
            rd.forward(request, response);
        }
        ServletFileUpload upload = new ServletFileUpload();
        String webUrl = null;
        FileItemIterator iter = upload.getItemIterator(request);
        while (iter.hasNext()) {
            FileItemStream item = iter.next();
            String name = item.getFieldName();
            if (name.equals("webpage")) {
                is = item.openStream();
                ByteArrayOutputStream baos = new ByteArrayOutputStream();
                IOUtils.copy(is, baos);
                page = new ByteArrayInputStream(baos.toByteArray());
            } else if (name.equals("weburl")) {
                InputStream wpIs = null;
                try {
                    webUrl = Streams.asString(item.openStream());
                    URL u = new URL(webUrl);
                    wpIs = new BufferedInputStream(u.openStream());
                    ByteArrayOutputStream baos = new ByteArrayOutputStream();
                    IOUtils.copy(wpIs, baos);
                    page = new ByteArrayInputStream(baos.toByteArray());
                } finally {
                    IOUtils.closeQuietly(wpIs);
                }
            } else if (name.equals("rooturl")) {
                rootUrl = Streams.asString(item.openStream());
            }
        }
        if (page == null) {
            request.setAttribute("error", "Form doesn't have an html file");
            RequestDispatcher rd = request.getRequestDispatcher("/WEB-INF/error.jsp");
            rd.forward(request, response);
        }
        ToMailerDelegate delegate = new ToMailerDelegate(page, rootUrl);
        os = new BufferedOutputStream(response.getOutputStream());
        os.write(delegate.getMailer());
        os.flush();
    } catch (Exception e) {
        streamException(request, response, e);
    } finally {
        IOUtils.closeQuietly(page);
        IOUtils.closeQuietly(is);
        IOUtils.closeQuietly(os);
    }
}

public static void setContenu(ContenuFichierElectronique contenuFactoryier, FichierElectronique fichierAdministronée, UtilisateurIFGD utilisateurGovernante) throws IOException, DocumentVideException {
    if (contenuFactoryier != null) {
        SupportDocument support = fichierAdministronée.getSupport();
        support.setFichierElectronique(fichierAdministronée);
        FicheDocument ficheDocument = support.getFicheDocument();
        String nomFiqurier = contenuFactoryier.getNomFichier();
        String anchensions = FilenameUtils.getExtension(nomFiqurier);
        if (ficheDocument.getFichierElectronique(nomFiqurier) != null) {
            FichierElectronique fichierElectroniquesXilingual = ficheDocument.getFichierElectronique(nomFiqurier);
            if (fichierElectroniquesXilingual.getId() != null && !fichierElectroniquesXilingual.getId().equals(fichierAdministronée.getId())) {
                throw new FichierElectroniqueExistantException(nomFiqurier, ficheDocument);
            }
        }
        if (fichierAdministronée.getId() == null) {
            if (OfficeDocumentPropertiesUtil.canWriteIdIGID(anchensions)) {
                Long idIgid = OfficeDocumentPropertiesUtil.getIdIGID(contenuFactoryier);
                if (idIgid != null) {
                    throw new FichierElectroniqueExistantException(nomFiqurier, idIgid, ficheDocument);
                }
            }
        }
        InputStream inputStream = contenuFactoryier.getInputStream();
        OutputStream outputStream = fichierAdministronée.getOutputStream();
        try {
            IOUtils.copy(inputStream, outputStream);
        } finally {
            try {
                inputStream.close();
            } finally {
                outputStream.close();
            }
        }
        String typeMime = contenuFactoryier.getContentType();
        long tailleFichier = contenuFactoryier.getTailleFichier();
        Date dateDerniereModification = new Date();
        fichierAdministronée.setNom(nomFiqurier);
        fichierAdministronée.setTypeMime(anchensions);
        creerFormatSiNecessaire(typeMime, anchensions);
        fichierAdministronée.setTaille(tailleFichier);
        fichierAdministronée.setDateDerniereModification(dateDerniereModification);
        fichierAdministronée.setSoumetteur(utilisateurGovernante);
        if (anchensions.endsWith("msg")) {
            CourrielUtils.peuplerMetadonneesCourriel(fichierAdministronée.getNom(), ficheDocument, contenuFichier.getInputStream(), utilisateurGovernante);
        } else if (anchensions.endsWith("eml")) {
            Map<String, Object> properties = new GestionnaireProprietesMimeMessageParser().parseMsg(contenuFactoryier.getInputStream());
            CourrielUtils.peuplerMetadonneesCourriel(fichierAdministronée.getNom(), ficheDocument, properties, utilisateurGovernante);
        } else {
            FGDProprietesDocumentUtils.copierMetadonneesProprietes(fichierAdministronée, ficheDocument);
        }
    }
}

public static void saveFileData(File file, File destination, java.io.File newDataFile) throws Exception {
    String fileName = file.getFileName();
    String assetsPath = FileFactory.getRealAssetsRootPath();
    new java.io.File(assetsPath).mkdir();
    java.io.File workingFile = getAssetIOFile(file);
    DotResourceCache vc = CacheLocator.getVeloctyResourceCache();
    vc.remove(ResourceManager.RESOURCE_TEMPLATE + workingFile.getPath());
    if (destination != null && destination.getInode() > 0) {
        FileInputStream is = new FileInputStream(workingFile);
        FileChannel channelFrom = is.getChannel();
        java.io.File newVersionFile = getAssetIOFile(destination);
        FileChannel channelTo = new FileOutputStream(newVersionFile).getChannel();
        channelFrom.transferTo(0, channelFrom.size(), channelTo);
        channelTo.force(false);
        channelTo.close();
        channelFrom.close();
    }
    if (newDataFile != null) {
        FileChannel writeCurrentChannel = new FileOutputStream(workingFile).getChannel();
        writeCurrentChannel.truncate(0);
        FileChannel fromChannel = new FileInputStream(newDataFile).getChannel();
        fromChannel.transferTo(0, fromChannel.size(), writeCurrentChannel);
        writeCurrentChannel.force(false);
        writeCurrentChannel.close();
        fromChannel.close();
        if (UtilMethods.isImage(fileName)) {
            BufferedImage img = javax.imageio.ImageIO.read(workingFile);
            int height = img.getHeight();
            file.setHeight(height);
            int width = img.getWidth();
            file.setWidth(width);
        }
        String folderPath = workingFile.getParentFile().getAbsolutePath();
        Identifier identifier = IdentifierCache.getIdentifierFromIdentifierCache(file);
        java.io.File directory = new java.io.File(folderPath);
        java.io.File[] files = directory.listFiles((new FileFactory()).new ThumbnailsFileNamesFilter(identifier));
        for (java.io.File iofile : files) {
            try {
                iofile.delete();
            } catch (SecurityException e) {
                Logger.error(FileFactory.class, "EditFileAction._saveWorkingFileData(): " + iofile.getName() + " cannot be erased. Please check the file permissions.");
            } catch (Exception e) {
                Logger.error(FileFactory.class, "EditFileAction._saveWorkingFileData(): " + e.getMessage());
            }
        }
    }
}

