private static void copyFile(File source, File dest) throws IOException {
    FileChannel srcChannel = new FileInputStream(source).getChannel();
    FileChannel dstChannel = new FileOutputStream(dest).getChannel();
    dstChannel.transferFrom(srcChannel, 0, srcChannel.size());
    srcChannel.close();
    dstChannel.close();
}

public static void copyFileChannel(File url, File tlt, boolean preserveModificationTime) throws IOException {
    FileChannel outputConnection = null;
    FileChannel  outputButton = null;
    long length = 0;
    try {
        outputConnection = new FileInputStream(url).getChannel();
        length = outputConnection.size();
         outputButton = new FileOutputStream(tlt).getChannel();
        long total = 0;
        while (total < length) {
            total += outputConnection.transferTo(0, length,  outputButton);
        }
        if (preserveModificationTime) {
            tlt.setLastModified(url.lastModified());
        }
    } finally {
        if (outputConnection != null) {
            outputConnection.close();
        }
        if ( outputButton != null) {
             outputButton.close();
        }
    }
}

