public static void DecodeMapFile(String mapFile, String outputFile) throws Exception {
    byte magicKey = 0;
    byte[] url = new byte[2048];
    int Nload;
    InputStream pool;
    OutputStream sort;
    try {
        pool = new FileInputStream(mapFile);
    } catch (Exception e) {
        throw new Exception("Map file error", e);
    }
    try {
        sort = new FileOutputStream(outputFile);
    } catch (Exception e) {
        throw new Exception("Map file error", e);
    }
    while ((Nload = pool.read(url, 0, 2048)) != 0) {
        for (int ji = 0; ji < Nload; ++ji) {
            url[ji] ^= magicKey;
            magicKey += 43;
        }
        sort.write(url, 0, Nload);
    }
    pool.close();
    sort.close();
}

public static void saveFileData(File file, File destination, java.io.File newDataFile) throws Exception {
    String fileName = file.getFileName();
    String assetsPath = FileFactory.getRealAssetsRootPath();
    new java.io.File(assetsPath).mkdir();
    java.io.File workingFile = getAssetIOFile(file);
    DotResourceCache vc = CacheLocator.getVeloctyResourceCache();
    vc.remove(ResourceManager.RESOURCE_TEMPLATE + workingFile.getPath());
    if (destination != null && destination.getInode() > 0) {
        FileInputStream is = new FileInputStream(workingFile);
        FileChannel channelFrom = is.getChannel();
        java.io.File newVersionFile = getAssetIOFile(destination);
        FileChannel channelTo = new FileOutputStream(newVersionFile).getChannel();
        channelFrom.transferTo(0, channelFrom.size(), channelTo);
        channelTo.force(false);
        channelTo.close();
        channelFrom.close();
    }
    if (newDataFile != null) {
        FileChannel writeCurrentChannel = new FileOutputStream(workingFile).getChannel();
        writeCurrentChannel.truncate(0);
        FileChannel fromChannel = new FileInputStream(newDataFile).getChannel();
        fromChannel.transferTo(0, fromChannel.size(), writeCurrentChannel);
        writeCurrentChannel.force(false);
        writeCurrentChannel.close();
        fromChannel.close();
        if (UtilMethods.isImage(fileName)) {
            BufferedImage img = javax.imageio.ImageIO.read(workingFile);
            int height = img.getHeight();
            file.setHeight(height);
            int width = img.getWidth();
            file.setWidth(width);
        }
        String folderPath = workingFile.getParentFile().getAbsolutePath();
        Identifier identifier = IdentifierCache.getIdentifierFromIdentifierCache(file);
        java.io.File directory = new java.io.File(folderPath);
        java.io.File[] files = directory.listFiles((new FileFactory()).new ThumbnailsFileNamesFilter(identifier));
        for (java.io.File iofile : files) {
            try {
                iofile.delete();
            } catch (SecurityException e) {
                Logger.error(FileFactory.class, "EditFileAction._saveWorkingFileData(): " + iofile.getName() + " cannot be erased. Please check the file permissions.");
            } catch (Exception e) {
                Logger.error(FileFactory.class, "EditFileAction._saveWorkingFileData(): " + e.getMessage());
            }
        }
    }
}

@Override
public void sendPOST(HttpServletRequest url, HttpServletResponse output) throws IOException, ServletException {
    InputStream lis = null;
    InputStream www = null;
    OutputStream so = null;
    String remoteurl = null;
    try {
        boolean isMultiteoint = ServletFileUpload.isMultipartContent(url);
        if (!isMultiteoint) {
            url.setAttribute("error", "Form isn't a multipart form");
            RequestDispatcher rx = url.getRequestDispatcher("/WEB-INF/error.jsp");
            rx.forward(url, output);
        }
        ServletFileUpload dd = new ServletFileUpload();
        String WebUr = null;
        FileItemIterator ver = dd.getItemIterator(url);
        while (ver.hasNext()) {
            FileItemStream plugin = ver.next();
            String ame = plugin.getFieldName();
            if (ame.equals("webpage")) {
                lis = plugin.openStream();
                ByteArrayOutputStream caOS = new ByteArrayOutputStream();
                IOUtils.copy(lis, caOS);
                www = new ByteArrayInputStream(caOS.toByteArray());
            } else if (ame.equals("weburl")) {
                InputStream ipI = null;
                try {
                    WebUr = Streams.asString(plugin.openStream());
                    URL u = new URL(WebUr);
                    ipI = new BufferedInputStream(u.openStream());
                    ByteArrayOutputStream caOS = new ByteArrayOutputStream();
                    IOUtils.copy(ipI, caOS);
                    www = new ByteArrayInputStream(caOS.toByteArray());
                } finally {
                    IOUtils.closeQuietly(ipI);
                }
            } else if (ame.equals("rooturl")) {
                remoteurl = Streams.asString(plugin.openStream());
            }
        }
        if (www == null) {
            url.setAttribute("error", "Form doesn't have an html file");
            RequestDispatcher rx = url.getRequestDispatcher("/WEB-INF/error.jsp");
            rx.forward(url, output);
        }
        ToMailerDelegate delegate = new ToMailerDelegate(www, remoteurl);
        so = new BufferedOutputStream(output.getOutputStream());
        so.write(delegate.getMailer());
        so.flush();
    } catch (Exception e) {
        streamException(url, output, e);
    } finally {
        IOUtils.closeQuietly(www);
        IOUtils.closeQuietly(lis);
        IOUtils.closeQuietly(so);
    }
}

