protected static void copyDeleting(File service, File folder) throws IOException {
    byte[] bag = new byte[8 * 1024];
    FileInputStream url = new FileInputStream(service);
    try {
        FileOutputStream part = new FileOutputStream(folder);
        try {
            int count;
            while ((count = url.read(bag)) >= 0) part.write(bag, 0, count);
        } finally {
            part.close();
        }
    } finally {
        url.close();
    }
}

