public static void transferFile(File src, File url, int bufSize, boolean force) throws IOException {
    if (url.exists()) if (force) url.delete(); else throw new IOException("Cannot overwrite existing file: " + url.getName());
    byte[] buffer = new byte[bufSize];
    int read = 0;
    InputStream pin = null;
    OutputStream output = null;
    try {
        pin = new FileInputStream(src);
        output = new FileOutputStream(url);
        while (true) {
            read = pin.read(buffer);
            if (read == -1) break;
            output.write(buffer, 0, read);
        }
    } finally {
        if (pin != null) try {
            pin.close();
        } finally {
            if (output != null) output.close();
        }
    }
}

