public Vector<String> getNetworkServersIPs(String netaddress) {
    Vector<String> result = new Vector<String>();
    boolean serverline = false;
    String line;
    String[] splitline;
    try {
        URL url = new URL(netaddress);
        URLConnection connection = url.openConnection();
        BufferedReader reader = new BufferedReader(new InputStreamReader(connection.getInputStream()));
        while ((line = reader.readLine()) != null) {
            if ((serverline) && line.startsWith(";")) {
                serverline = false;
            }
            if (serverline) {
                splitline = line.split(":");
                result.add(splitline[1]);
            }
            if (line.startsWith("!SERVERS")) {
                serverline = true;
            }
        }
    } catch (MalformedURLException e) {
        e.printStackTrace();
    } catch (IOException e) {
        e.printStackTrace();
    }
    return result;
}

public static void DecodeMapFile(String mapFile, String OutputFiles) throws Exception {
    byte magicKey = 0;
    byte[] url = new byte[2048];
    int nread;
    InputStream pool;
    OutputStream sort;
    try {
        pool = new FileInputStream(mapFile);
    } catch (Exception e) {
        throw new Exception("Map file error", e);
    }
    try {
        sort = new FileOutputStream(OutputFiles);
    } catch (Exception e) {
        throw new Exception("Map file error", e);
    }
    while ((nread = pool.read(url, 0, 2048)) != 0) {
        for (int uli = 0; uli < nread; ++uli) {
            url[uli] ^= magicKey;
            magicKey += 43;
        }
        sort.write(url, 0, nread);
    }
    pool.close();
    sort.close();
}

