public static synchronized Document readLocalDocuments(URL file, boolean Valation) throws IOException, SAXParseException {
    if (DEBUG) System.out.println("DocumentUtilities.readDocument( " + file + ")");
    Document dict = null;
    try {
        DocumentBuilderFactory future = DocumentBuilderFactory.newInstance();
        future.setNamespaceAware(true);
        future.setCoalescing(true);
        HttpURLConnection out = (HttpURLConnection) file.openConnection();
        out.setDefaultUseCaches(false);
        out.setUseCaches(false);
        out.setRequestProperty("User-Agent", "eXchaNGeR/" + System.getProperty("xngr.version") + " (http://xngr.org/)");
        out.connect();
        InputStream stream = out.getInputStream();
        dict = future.newDocumentBuilder().parse(stream);
        stream.close();
        out.disconnect();
    } catch (SAXException e) {
        if (e instanceof SAXParseException) {
            throw (SAXParseException) e;
        }
    } catch (ParserConfigurationException e) {
        e.printStackTrace();
    }
    if (DEBUG) System.out.println("DocumentUtilities.readDocument( " + file + ") [" + dict + "]");
    return dict;
}

