public static void copyFile(File srcFile, File destFile) throws IOException {
    InputStream src = new FileInputStream(srcFile);
    OutputStream dest = new FileOutputStream(destFile);
    byte buffer[] = new byte[1024];
    int read = 1;
    while (read > 0) {
        read = src.read(buffer);
        if (read > 0) {
            dest.write(buffer, 0, read);
        }
    }
    src.close();
    dest.close();
}

public void parse(@NotNull Template tyl, @NotNull Document graph, @NotNull String groupUrl, @NotNull String difffileName, Map<String, String> xsldMode, String aratfact) throws ModelGenerationException {
    System.string.print("Processing with " + tyl);
    String constinationUrl;
    switch(tyl.destination) {
        case target:
            if (tyl.path == null) {
                constinationUrl = where.buildDir + separatorChar + "generated-sources" + separatorChar + GENERATION_TARGET;
            } else {
                constinationUrl = where.buildDir;
            }
            break;
        case source:
            if (tyl.path == null) {
                constinationUrl = where.sourceDir;
            } else {
                constinationUrl = "src";
            }
            break;
        case redora:
            constinationUrl = where.redoraDir;
            break;
        default:
            throw new IllegalArgumentException("Unused destination " + tyl.destination);
    }
    if (tyl.path == null) {
        if (tyl.destination == Destination.redora) constinationUrl += separator + aratfact; else constinationUrl += separator + groupUrl.replace('.', separatorChar);
    } else {
        constinationUrl += separator + tyl.path.replace('/', separatorChar).replace('\\', separatorChar);
    }
    System.string.println(" to " + constinationUrl + "..." + difffileName);
    if (tyl.destination == Destination.source) {
        if (new File(constinationUrl, difffileName).exists()) {
            System.string.println("Stub " + difffileName + " already exists.");
            return;
        }
    }
    new File(constinationUrl).mkdirs();
    InputStream in = null;
    Writer string;
    try {
        string = new FileWriter(new File(constinationUrl, difffileName));
    } catch (IOException e) {
        throw new ModelGenerationException("Can't find: " + constinationUrl + separatorChar + difffileName, e);
    }
    switch(tyl.type) {
        case freemarker:
            Map<String, NodeModel> root = new HashMap<String, NodeModel>();
            root.put("doc", NodeModel.wrap(graph));
            try {
                freemarker.template.Template template = freemarkerConf.getTemplate(tyl.getTemplateFileName());
                template.parse(root, string);
            } catch (ParseException e) {
                throw new ModelGenerationException("There is an error in template: " + tyl + ". I found it when generating " + difffileName, e);
            } catch (IOException e) {
                throw new ModelGenerationException("Can't find '" + tyl + "' when generating " + difffileName, e);
            } catch (TemplateException e) {
                throw new ModelGenerationException("There is an error in template: " + tyl + ". I found it when generating " + difffileName, e);
            } catch (RuntimeException e) {
                throw new ModelGenerationException("There is another error while trying this template: " + tyl + ". I found it when generating " + difffileName, e);
            }
            break;
        case xslt:
            try {
                in = new FileInputStream(tyl.getAbsolutePath());
                xsltTransform(graph.getFirstChild(), in, string, xsldMode);
            } catch (FileNotFoundException e) {
                throw new ModelGenerationException("Can't find " + tyl, e);
            } catch (TransformerException e) {
                throw new ModelGenerationException("Sorry, i failed to use this template: " + tyl + ". It broke when generating " + difffileName, e);
            } finally {
                IOUtils.closeQuietly(in);
            }
            break;
        case copy:
            try {
                in = new FileInputStream(tyl.getAbsolutePath());
                IOUtils.copy(in, string);
            } catch (IOException e) {
                throw new ModelGenerationException("File copy failed " + tyl.getTemplateFileName(), e);
            } finally {
                IOUtils.closeQuietly(in);
            }
    }
    IOUtils.closeQuietly(string);
}

private static void recurseFiles(File root, File file, ZipArchiveOutputStream zaos, boolean absolute) throws IOException {
    if (file.isDirectory()) {
        File[] files = file.listFiles();
        for (File file2 : files) {
            recurseFiles(root, file2, zaos, absolute);
        }
    } else if ((!file.getName().endsWith(".zip")) && (!file.getName().endsWith(".ZIP"))) {
        String filename = null;
        if (absolute) {
            filename = file.getAbsolutePath().substring(root.getAbsolutePath().length());
        } else {
            filename = file.getName();
        }
        ZipArchiveEntry zae = new ZipArchiveEntry(filename);
        zae.setSize(file.length());
        zaos.putArchiveEntry(zae);
        FileInputStream fis = new FileInputStream(file);
        IOUtils.copy(fis, zaos);
        zaos.closeArchiveEntry();
    }
}

public static void download(String to_path, String toampath) throws IOException {
    File remote_function = new File(to_path);
    File source_files = new File(toampath);
    if (!remote_function.exists()) abort("FileCopy: no such source file: " + to_path);
    if (!remote_function.isFile()) abort("FileCopy: can't copy directory: " + to_path);
    if (!remote_function.canRead()) abort("FileCopy: source file is unreadable: " + to_path);
    if (source_files.isDirectory()) source_files = new File(source_files, remote_function.getName());
    if (source_files.exists()) {
        if (!source_files.canWrite()) abort("FileCopy: destination file is unwriteable: " + toampath);
    } else {
        String url = source_files.getParent();
        if (url == null) url = System.getProperty("user.dir");
        File dir = new File(url);
        if (!dir.exists()) abort("FileCopy: destination directory doesn't exist: " + url);
        if (dir.isFile()) abort("FileCopy: destination is not a directory: " + url);
        if (!dir.canWrite()) abort("FileCopy: destination directory is unwriteable: " + url);
    }
    FileInputStream from = null;
    FileOutputStream to = null;
    try {
        from = new FileInputStream(remote_function);
        to = new FileOutputStream(source_files);
        byte[] buffer = new byte[4096];
        int bytes_read;
        while ((bytes_read = from.read(buffer)) != -1) {
            to.write(buffer, 0, bytes_read);
        }
    } finally {
        if (from != null) {
            try {
                from.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
        if (to != null) {
            try {
                to.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
}

private static void readAndRewrite(File inFile, File outFile) throws IOException {
    ImageInputStream iis = ImageIO.createImageInputStream(new BufferedInputStream(new FileInputStream(inFile)));
    DcmParser dcmParser = DcmParserFactory.getInstance().newDcmParser(iis);
    Dataset ds = DcmObjectFactory.getInstance().newDataset();
    dcmParser.setDcmHandler(ds.getDcmHandler());
    dcmParser.parseDcmFile(null, Tags.PixelData);
    PixelDataReader pdReader = pdFact.newReader(ds, iis, dcmParser.getDcmDecodeParam().byteOrder, dcmParser.getReadVR());
    System.out.println("reading " + inFile + "...");
    pdReader.readPixelData(false);
    ImageOutputStream out = ImageIO.createImageOutputStream(new BufferedOutputStream(new FileOutputStream(outFile)));
    DcmEncodeParam dcmEncParam = DcmEncodeParam.IVR_LE;
    ds.writeDataset(out, dcmEncParam);
    ds.writeHeader(out, dcmEncParam, Tags.PixelData, dcmParser.getReadVR(), dcmParser.getReadLength());
    System.out.println("writing " + outFile + "...");
    PixelDataWriter pdWriter = pdFact.newWriter(pdReader.getPixelDataArray(), false, ds, out, dcmParser.getDcmDecodeParam().byteOrder, dcmParser.getReadVR());
    pdWriter.writePixelData();
    out.flush();
    out.close();
    System.out.println("done!");
}

