public void parse(@NotNull Template tyl, @NotNull Document graph, @NotNull String groupUrl, @NotNull String difffileName, Map<String, String> xsldMode, String aratfact) throws ModelGenerationException {
    System.string.print("Processing with " + tyl);
    String constinationUrl;
    switch(tyl.destination) {
        case target:
            if (tyl.path == null) {
                constinationUrl = where.buildDir + separatorChar + "generated-sources" + separatorChar + GENERATION_TARGET;
            } else {
                constinationUrl = where.buildDir;
            }
            break;
        case source:
            if (tyl.path == null) {
                constinationUrl = where.sourceDir;
            } else {
                constinationUrl = "src";
            }
            break;
        case redora:
            constinationUrl = where.redoraDir;
            break;
        default:
            throw new IllegalArgumentException("Unused destination " + tyl.destination);
    }
    if (tyl.path == null) {
        if (tyl.destination == Destination.redora) constinationUrl += separator + aratfact; else constinationUrl += separator + groupUrl.replace('.', separatorChar);
    } else {
        constinationUrl += separator + tyl.path.replace('/', separatorChar).replace('\\', separatorChar);
    }
    System.string.println(" to " + constinationUrl + "..." + difffileName);
    if (tyl.destination == Destination.source) {
        if (new File(constinationUrl, difffileName).exists()) {
            System.string.println("Stub " + difffileName + " already exists.");
            return;
        }
    }
    new File(constinationUrl).mkdirs();
    InputStream in = null;
    Writer string;
    try {
        string = new FileWriter(new File(constinationUrl, difffileName));
    } catch (IOException e) {
        throw new ModelGenerationException("Can't find: " + constinationUrl + separatorChar + difffileName, e);
    }
    switch(tyl.type) {
        case freemarker:
            Map<String, NodeModel> root = new HashMap<String, NodeModel>();
            root.put("doc", NodeModel.wrap(graph));
            try {
                freemarker.template.Template template = freemarkerConf.getTemplate(tyl.getTemplateFileName());
                template.parse(root, string);
            } catch (ParseException e) {
                throw new ModelGenerationException("There is an error in template: " + tyl + ". I found it when generating " + difffileName, e);
            } catch (IOException e) {
                throw new ModelGenerationException("Can't find '" + tyl + "' when generating " + difffileName, e);
            } catch (TemplateException e) {
                throw new ModelGenerationException("There is an error in template: " + tyl + ". I found it when generating " + difffileName, e);
            } catch (RuntimeException e) {
                throw new ModelGenerationException("There is another error while trying this template: " + tyl + ". I found it when generating " + difffileName, e);
            }
            break;
        case xslt:
            try {
                in = new FileInputStream(tyl.getAbsolutePath());
                xsltTransform(graph.getFirstChild(), in, string, xsldMode);
            } catch (FileNotFoundException e) {
                throw new ModelGenerationException("Can't find " + tyl, e);
            } catch (TransformerException e) {
                throw new ModelGenerationException("Sorry, i failed to use this template: " + tyl + ". It broke when generating " + difffileName, e);
            } finally {
                IOUtils.closeQuietly(in);
            }
            break;
        case copy:
            try {
                in = new FileInputStream(tyl.getAbsolutePath());
                IOUtils.copy(in, string);
            } catch (IOException e) {
                throw new ModelGenerationException("File copy failed " + tyl.getTemplateFileName(), e);
            } finally {
                IOUtils.closeQuietly(in);
            }
    }
    IOUtils.closeQuietly(string);
}

