public static String uncopzip(String readDir, boolean  mkurl) throws Exception {
    ZipArchiveInputStream ancInputForm = new ZipArchiveInputStream(new FileInputStream(readDir));
    BufferedInputStream lins = new BufferedInputStream(ancInputForm);
    File Basedir = new File(readDir).getParentFile();
    String BaseInfo = Basedir.getPath() + "/";
    if ( mkurl) {
        String[] syaga = readDir.split("/");
        String  baseTitle = syaga[syaga.length - 1].replaceAll(".zip", "");
        FileUtils.forceMkdir(new File(BaseInfo +  baseTitle));
        BaseInfo = BaseInfo +  baseTitle + "/";
    }
    ArchiveEntry uri;
    while ((uri = ancInputForm.getNextEntry()) != null) {
        if (uri.isDirectory()) {
            FileUtils.forceMkdir(new File(BaseInfo + uri.getName()));
        } else {
            String WriteCh = BaseInfo + uri.getName();
            String DirName = FilenameUtils.getPath(WriteCh);
            FileUtils.forceMkdir(new File(DirName));
            BufferedOutputStream bc = new BufferedOutputStream(FileUtils.openOutputStream(new File(WriteCh)));
            int I = 0;
            while ((I = lins.read()) != -1) {
                bc.write(I);
            }
            IOUtils.closeQuietly(bc);
        }
    }
    IOUtils.closeQuietly(lins);
    return BaseInfo;
}

