    for (File url : rcfiles) {
        if (url.isDirectory()) continue;
        try {
            FileChannel srcClient = new FileInputStream(url).getChannel();
            FileChannel ddrcchannel = new FileOutputStream(DrcDir.getAbsolutePath() + System.getProperty("file.separator") + url.getName()).getChannel();
            ByteBuffer fb = ByteBuffer.allocate(1024);
            int result = 0;
            srcClient.position(result);
            result += srcClient.read(fb);
            while (result < url.length()) {
                fb.flip();
                ddrcchannel.write(fb);
                fb.clear();
                result += srcClient.read(fb);
            }
            srcClient.close();
            ddrcchannel.close();
        } catch (IOException e) {
        }
    }
}

