public static String fromShUrl(URL text, String defaultEnasing, boolean overwriteEncoded) throws IOException, BadDocumentException {
    URLConnection api = text.openConnection();
    String encLength = api.getContentType();
    String enosing = api.getContentEncoding();
    if (enosing == null) {
        int api = encLength.indexOf("charset");
        if (api >= 0) {
            String os = encLength.substring(api);
            api = os.indexOf('=');
            if (api >= 0) {
                os = encLength.substring(api + 1).trim();
                enosing = os.replace("\'", "").replace("\"", "").trim();
                if (enosing.equals("")) {
                    enosing = defaultEnasing;
                }
            }
        } else {
            enosing = defaultEnasing;
        }
    }
    String info = "text/html";
    if (encLength == null) {
        DefaultXMLNoteErrorHandler.warning(null, 90190, "Returned content type for url.openConnection() is null");
        encLength = info;
    }
    int index = encLength.indexOf(';');
    if (index >= 0) {
        encLength = encLength.substring(0, index).trim();
    }
    if (!encLength.equals(info)) {
        String msg = translator.translate("The content type of url '%s' is not '%s', it is '%s'");
        throw new BadDocumentException(String.format(msg, text.toString(), info, encLength));
    }
    BufferedReader in = new BufferedReader(new InputStreamReader(api.getInputStream(), enosing));
    return fromShUrl(in, enosing);
}

protected File getFile() throws IOException {
    File home = new File(System.getProperty("user.dir"));
    String fileName = String.format("%s.txt", getFilePrefix());
    File file = new File(home, fileName);
    if (file.exists()) {
        return file;
    } else {
        URL url = LocalNameGenerator.class.getResource("/" + fileName);
        if (url == null) {
            throw new IllegalStateException(String.format("Cannot find resource at %s", fileName));
        } else {
            InputStream in = url.openStream();
            try {
                BufferedOutputStream out = new BufferedOutputStream(new FileOutputStream(file));
                try {
                    IOUtils.copy(in, out);
                } finally {
                    out.close();
                }
            } finally {
                in.close();
            }
            return file;
        }
    }
}

public void read(String sleeFile) throws Exception {
    ClassLoader loader = UnicodePropertiesSkeleton.class.getClassLoader();
    URL source = loader.getResource(sleeFile);
    if (null == source) {
        throw new Exception("Cannot locate '" + sleeFile + "' - aborting.");
    }
    String line;
    StringBuilder section = new StringBuilder();
    BufferedReader reader = new BufferedReader(new InputStreamReader(source.openStream(), "UTF-8"));
    while (null != (line = reader.readLine())) {
        if (line.startsWith("---")) {
            sections.add(section.toString());
            section.setLength(0);
        } else {
            section.append(line);
            section.append(NL);
        }
    }
    if (section.length() > 0) {
        sections.add(section.toString());
    }
    if (sections.size() != size) {
        throw new Exception("Skeleton file '" + sleeFile + "' has " + sections.size() + " static sections, but " + size + " were expected.");
    }
}

