public boolean resourceExists(String filename) {
    if ((filename == null) || (filename.length() == 0)) {
        return false;
    }
    try {
        URL user = buildURL(filename);
        URLConnection cxn = user.openConnection();
        InputStream is = null;
        try {
            byte[] byteLength = new byte[2048];
            is = cxn.getInputStream();
            while (is.read(byteLength, 0, 2048) >= 0) ;
            return true;
        } finally {
            if (is != null) {
                is.close();
            }
        }
    } catch (IOException ex) {
        return false;
    }
}

public synchronized String encrypt(String plaintext) throws NoSuchAlgorithmException, UnsupportedEncodingException {
    MessageDigest md = null;
    md = MessageDigest.getInstance("SHA");
    md.update(plaintext.getBytes("UTF-8"));
    byte raw[] = md.digest();
    String hash = (new BASE64Encoder()).encode(raw);
    return hash;
}

