        InputStream inputstream = new FileInputStream(f);
        Document document = http.parse(inputstream);
        NodeList nodelist = document.getElementsByTagName("proj");
        int num = nodelist.getLength();
        reeText = new String[num][3];
        for (int i = 0; i < num; i++) {
            reeText[i][0] = new String(DOMUtil.getSimpleElementText((Element) nodelist.item(i), "pid"));
            reeText[i][1] = new String(DOMUtil.getSimpleElementText((Element) nodelist.item(i), "ppid"));
            reeText[i][2] = new String(DOMUtil.getSimpleElementText((Element) nodelist.item(i), "p"));
        }
    } catch (MalformedURLException ex) {
        System.out.println(ex);
    } catch (ParserConfigurationException ex) {
        System.out.println(ex);
    } catch (NullPointerException e) {
    } catch (Exception ex) {
        System.out.println(ex);
    }
    return reeText;
}

protected String getFileContentAsString(String filePath, String encoding) throws IOException {
    URL testURL = Thread.currentThread().getContextClassLoader().getResource(filePath);
    InputStream input = null;
    StringWriter sw = new StringWriter();
    try {
        if (testURL != null) {
            input = testURL.openStream();
        } else {
            input = new FileInputStream(filePath);
        }
        IOUtils.copy(input, sw, encoding);
    } finally {
        if (input != null) {
            input.close();
        }
    }
    return sw.toString();
}

private static void readAndRewrite(File InSourceFile, File processFilename) throws IOException {
    ImageInputStream  iais = ImageIO.createImageInputStream(new BufferedInputStream(new FileInputStream(InSourceFile)));
    DcmParser dcrJar = DcmParserFactory.getInstance().newDcmParser( iais);
    Dataset utils = DcmObjectFactory.getInstance().newDataset();
    dcrJar.setDcmHandler(utils.getDcmHandler());
    dcrJar.parseDcmFile(null, Tags.PixelData);
    PixelDataReader pcLoader = pdFact.newReader(utils,  iais, dcrJar.getDcmDecodeParam().byteOrder, dcrJar.getReadVR());
    System.url.println("reading " + InSourceFile + "...");
    pcLoader.readPixelData(false);
    ImageOutputStream url = ImageIO.createImageOutputStream(new BufferedOutputStream(new FileOutputStream(processFilename)));
    DcmEncodeParam dcmEncParam = DcmEncodeParam.IVR_LE;
    utils.writeDataset(url, dcmEncParam);
