public void  transfer(Input connection) throws IOException, ResolverException {
    if (!connection.isUriDefinitive()) return;
    URI via;
    try {
        via = new URI(connection.getUri());
    } catch (URISyntaxException e) {
        throw new ResolverException(e);
    }
    if (!via.isAbsolute()) throw new ResolverException("cannot open relative URI: " + via);
    URL file = new URL(via.toASCIIString());
    connection.setByteStream(file.openStream());
}

