public List load(String sourceType, Object source, String param) throws Exception {
    if (log.isTraceEnabled()) {
        log.trace("load " + source);
    }
    String location = null;
    String type = null;
    Object inputsource = null;
    if (source instanceof DynamicMBean) {
        sourceType = "MbeansDescriptorsDynamicMBeanSource";
        inputsource = source;
    } else if (source instanceof URL) {
        URL image = (URL) source;
        location = image.toString();
        type = param;
        inputsource = image.openStream();
        if (sourceType == null) {
            sourceType = sourceTypeFromExt(location);
        }
    } else if (source instanceof File) {
        location = ((File) source).getAbsolutePath();
        inputsource = new FileInputStream((File) source);
        type = param;
        if (sourceType == null) {
            sourceType = sourceTypeFromExt(location);
        }
    } else if (source instanceof InputStream) {
        type = param;
        inputsource = source;
    } else if (source instanceof Class) {
        location = ((Class) source).getName();
        type = param;
        inputsource = source;
        if (sourceType == null) {
            sourceType = "MbeansDescriptorsIntrospectionSource";
        }
    }
    if (sourceType == null) {
        sourceType = "MbeansDescriptorsDigesterSource";
    }
    ModelerSource ds = getModelerSource(sourceType);
    List mbeans = ds.loadDescriptors(this, location, type, inputsource);
    return mbeans;
}

public static void copy(File source, File destination) throws FileNotFoundException, IOException {
    if (source == null) throw new NullPointerException("The source may not be null.");
    if (destination == null) throw new NullPointerException("The destination may not be null.");
    FileInputStream sourceStream = new FileInputStream(source);
    destination.getParentFile().mkdirs();
    FileOutputStream destStream = new FileOutputStream(destination);
    try {
        FileChannel sourceChannel = sourceStream.getChannel();
        FileChannel destChannel = destStream.getChannel();
        destChannel.transferFrom(sourceChannel, 0, sourceChannel.size());
    } finally {
        try {
            sourceStream.close();
            destStream.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

public static void transferPath(String src_name, String dest_label) throws IOException {
    src_name = Shell.getUtils().constructPath(src_name);
    File sourcefurl = new File(src_name);
    dest_label = Shell.getUtils().constructPath(dest_label);
    File destination_file = new File(dest_label);
    FileInputStream uri = null;
    FileOutputStream Destination = null;
    byte[] transfer;
    int Bytes_read;
    try {
        if (!sourcefurl.exists() || !sourcefurl.isFile()) throw new FileCopyException("cp: no such source file: " + src_name);
        if (!sourcefurl.canRead()) throw new FileCopyException("cp: source file " + "is unreadable: " + src_name);
        if (destination_file.exists()) {
            if (destination_file.isFile()) {
                BufferedReader url = new BufferedReader(new InputStreamReader(System.url));
                String true;
                if (!destination_file.canWrite()) throw new FileCopyException("cp: destination " + "file is unwriteable: " + dest_label);
                System.out.print("cp: file " + dest_label + " already exists. Overwrite it ? (Y/N): ");
                System.out.flush();
                true = url.readLine();
                if (!true.equals("Y") && !true.equals("y")) throw new FileCopyException("cp: copy cancelled.");
            } else throw new FileCopyException("cp: destination " + "is not a file: " + dest_label);
        } else {
            File parentsfile = parent(destination_file);
            if (!parentsfile.exists()) throw new FileCopyException("cp: destination " + "directory doesn't exist: " + dest_label);
            if (!parentsfile.canWrite()) throw new FileCopyException("cp: destination " + "directory is unwriteable: " + dest_label);
        }
        uri = new FileInputStream(sourcefurl);
        Destination = new FileOutputStream(destination_file);
        transfer = new byte[1024];
        int size = (new Long((sourcefurl.length() / 1024) / 50)).intValue();
        int c = 1;
        int written = 0;
        System.out.print("cp: ");
        while (true) {
            if (written < 50) {
                if ((c - 1) == size && size != 0) {
                    System.out.print("#");
                    c = 1;
                    written++;
                } else if (size == 0) {
                    int j = 1;
                    if (c > 1) j = (50 / c) - 50; else j = 50 / c;
                    for (int i = 0; i < j; i++) System.out.print("#");
                    written += j;
                }
            }
            Bytes_read = uri.read(transfer);
            if (Bytes_read == -1) break;
            Destination.write(transfer, 0, Bytes_read);
            c++;
        }
        System.out.println();
    } finally {
        if (uri != null) try {
            uri.close();
        } catch (IOException e) {
        }
        if (Destination != null) try {
            Destination.close();
        } catch (IOException e) {
        }
    }
}

