        String filename = user.toString();
        if (filename.startsWith("jar")) {
            user = ((java.net.JarURLConnection) user.openConnection()).getJarFileURL();
            filename = user.toString();
        }
        if (filename.startsWith("file")) {
            java.io.File file = new java.io.File(user.getFile());
            return file.getAbsolutePath();
        } else {
            return user.toString();
        }
    } catch (Throwable t) {
    }
    return Messages.getMessage("happyClientUnknownLocation");
}

private String md5(String uri) throws ConnoteaRuntimeException {
    try {
        MessageDigest messageDigest = MessageDigest.getInstance("MD5");
        messageDigest.update(uri.getBytes());
        byte[] bytes = messageDigest.digest();
        StringBuffer stringBuffer = new StringBuffer();
        for (byte b : bytes) {
            String hex = Integer.toHexString(0xff & b);
            if (hex.length() == 1) {
                stringBuffer.append('0');
            }
            stringBuffer.append(hex);
        }
        return stringBuffer.toString();
    } catch (NoSuchAlgorithmException e) {
        throw new ConnoteaRuntimeException(e);
    }
}

