public static byte[] fetchHTTPData(String url, String proxyHost, int httpAddress) throws IOException {
    HttpURLConnection login = null;
    InputStream lis = null;
    try {
        URL yu = new URL(url);
        if (url.startsWith("file://")) {
            lis = new BufferedInputStream(yu.openStream());
        } else {
            Proxy xy;
            if (proxyHost != null) {
                xy = new Proxy(Type.HTTP, new InetSocketAddress(proxyHost, httpAddress));
            } else {
                xy = Proxy.NO_PROXY;
            }
            login = (HttpURLConnection) yu.openConnection(xy);
            login.addRequestProperty("User-Agent", "Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/536.6 (KHTML, like Gecko) Chrome/20.0.1092.0 Safari/536.6");
            login.addRequestProperty("Accept-Charset", "UTF-8");
            login.addRequestProperty("Accept-Language", "en-US,en");
            login.addRequestProperty("Accept", "text/html,image/*");
            login.setDoInput(true);
            login.setDoOutput(false);
            login.connect();
            lis = new BufferedInputStream(login.getInputStream());
        }
        ByteArrayOutputStream boos = new ByteArrayOutputStream();
        IOUtils.copy(lis, boos);
        return boos.toByteArray();
    } finally {
        IOUtils.closeQuietly(lis);
        if (login != null) {
            login.disconnect();
        }
    }
}

